   142          
   143          // Migrate patients
   144          for (const patient of clinicPatientsFromLocal) {
   145            try {
   146              await DatabaseService.add('patients', patient);
   147              console.log(`SUCCESS: Migrated patient: ${patient.name}`);
   148            } catch (error) {
   149              console.error(`ERROR: Failed to migrate patient ${patient.name}:`, error);
   150            }
   151          }
   152          
   153          // Migrate reports
   154          const clinicReportsFromLocal = localStorageReports.filter(r => 
   155            r.clinicId === currentClinic.id || 
   156            r.clinicId == currentClinic.id || // eslint-disable-line eqeqeq
   157            String(r.clinicId) === String(currentClinic.id)
   158          );
   159          
   160          for (const report of clinicReportsFromLocal) {
   161            try {
   162              // Ensure report has required fields
   163              const reportToMigrate = {
   164                ...report,
   165                id: report.id || `report_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
   166                createdAt: report.createdAt || new Date().toISOString()
   167              };
   168              
   169              await DatabaseService.add('reports', reportToMigrate);
   170              console.log(`SUCCESS: Migrated report: ${report.fileName}`);
   171            } catch (error) {
   172              console.error(`ERROR: Failed to migrate report ${report.fileName}:`, error);
   173            }
   174          }
   175          
   176          // Reload data after migration
   177          clinicPatients = await DatabaseService.getPatientsByClinic(currentClinic.id);
   178          clinicReports = await DatabaseService.getReportsByClinic(currentClinic.id);
   179          
   180          console.log(`SUCCESS: Migration complete! Patients: ${clinicPatients.length}, Reports: ${clinicReports.length}`);
   181        }
   182      }
   183      
   184      // Calculate clinic usage
   185      const clinicUsage = {
   186        totalReports: clinicReports.length,
   187        reportsUsed: currentClinic.reportsUsed || 0,
   188        reportsAllowed: currentClinic.reportsAllowed || 10
   189      };
   190      
   191      console.log('DATA: Clinic data loaded:', {
   192        clinic: currentClinic.name,
   193        patients: clinicPatients.length,
   194        reports: clinicReports.length
   195      });
   196      
   197      setClinic(currentClinic);
   198      setPatients(clinicPatients);
   199      setReports(clinicReports);
   200      setUsage(clinicUsage);
   201      setDataLoaded(true); // Mark data as loaded
   202    } catch (error) {
   203      console.error('Error loading clinic data:', error);
   204    } finally {
   205      setLoading(false);
   206    }
   207  };
   208
   209  // Separate refresh function that forces a reload
   210  const refreshClinicData = async () => {
   211    console.log('REFRESH: Force refreshing clinic data...');
   212    setDataLoaded(false); // This will trigger a reload
   213    await loadClinicData();
   214  };
   215
   216  const renderContent = () => {
   217    switch (activeTab) {
   218      case 'overview':
   219        return <OverviewTab clinic={clinic} patients={patients} reports={reports} usage={usage} onRefresh={refreshClinicData} />;
   220      case 'patients':
   221        console.log('CLINIC: Rendering PatientManagement with clinicId:', clinic?.id);
   222        return <PatientManagement key={`patients-${clinic?.id}`} clinicId={clinic?.id} onUpdate={refreshClinicData} />;
   223      case 'reports':
   224        return <ReportViewer clinicId={clinic?.id} patients={patients} reports={reports} onUpdate={refreshClinicData} />;
   225      case 'usage':
   226        return <UsageTracking clinic={clinic} usage={usage} />;
   227      case 'analytics':
   228        return <AdvancedAnalytics clinicId={clinic?.id} clinic={clinic} />;
   229      case 'subscription':
   230        return <SubscriptionTab user={user} clinic={clinic} />;
   231      // case 'settings':
   232      //   return <ClinicSettings clinic={clinic} />;
   233      default:
   234        return <OverviewTab clinic={clinic} patients={patients} reports={reports} usage={usage} onRefresh={refreshClinicData} />;
   235    }
   236  };
   237
   238  const getPageTitle = () => {
   239    switch (activeTab) {
   240      case 'overview': return 'Clinic Dashboard';
   241      case 'patients': return 'Patient Management';
   242      case 'reports': return 'Reports & Files';
   243      case 'usage': return 'Usage Tracking';
   244      case 'analytics': return 'Advanced Analytics';
   245      case 'subscription': return 'Subscription & Billing';
   246      // case 'settings': return 'Clinic Settings';
   247      default: return 'Clinic Dashboard';
   248    }
   249  };
   250
   251  if (loading) {
   252    return (
   253      <DashboardLayout title="Loading...">
   254        <div className="flex items-center justify-center h-64">
   255          <div className="text-center">
   256            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
   257            <p className="mt-4 text-gray-600 dark:text-gray-400">Loading Clinic Portal...</p>
   258          </div>
   259        </div>
   260      </DashboardLayout>
   261    );
   262  }
   263
   264  return (
   265    <DashboardLayout title={getPageTitle()}>
   266      <div className="space-y-6">
   267        {renderContent()}
   268      </div>
   269    </DashboardLayout>
   270  );
   271};
   272
   273// Usage Tracking Component
   274const UsageTracking = ({ clinic, usage }) => {
   275  const usagePercentage = clinic?.reportsUsed && clinic?.reportsAllowed
   276    ? (clinic.reportsUsed / clinic.reportsAllowed) * 100
   277    : 0;
   278
   279  return (
   280    <div className="space-y-6">
   281      <div className="bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700 p-6">
   282        <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Usage Overview</h3>
   283
   284        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
   285          <div className="text-center">
   286            <div className="text-3xl font-bold text-[#323956] dark:text-blue-400">{clinic?.reportsUsed || 0}</div>
   287            <div className="text-sm text-gray-500 dark:text-gray-400">Reports Used</div>
   288          </div>
   289
   290          <div className="text-center">
   291            <div className="text-3xl font-bold text-[#323956] dark:text-blue-400">{clinic?.reportsAllowed || 10}</div>
   292            <div className="text-sm text-gray-500 dark:text-gray-400">Reports Allowed</div>
   293          </div>
   294
   295          <div className="text-center">
   296            <div className="text-3xl font-bold text-purple-600 dark:text-purple-400">{Math.max(0, (clinic?.reportsAllowed || 10) - (clinic?.reportsUsed || 0))}</div>
   297            <div className="text-sm text-gray-500 dark:text-gray-400">Remaining</div>
   298          </div>
   299        </div>
   300
   301        <div className="mt-6">
   302          <div className="flex justify-between text-sm text-gray-600 dark:text-gray-400 mb-2">
   303            <span>Usage Progress</span>
   304            <span>{usagePercentage.toFixed(1)}%</span>
   305          </div>
   306          <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
   307            <div
   308              className={`h-2 rounded-full transition-all duration-300 ${
   309                usagePercentage >= 90 ? 'bg-red-500 dark:bg-red-600' :
   310                usagePercentage >= 70 ? 'bg-yellow-500 dark:bg-yellow-600' : 'bg-[#323956] dark:bg-blue-500'
   311              }`}
   312              style={{ width: `${Math.min(usagePercentage, 100)}%` }}
   313            ></div>
   314          </div>
   315        </div>
   316      </div>
   317    </div>
   318  );
   319};
   320
   321// Clinic Settings Component
   322const ClinicSettings = ({ clinic }) => {
   323  const [formData, setFormData] = useState({
   324    name: clinic?.name || '',
   325    contactPerson: clinic?.contactPerson || clinic?.contact_person || '',
   326    email: clinic?.email || '',
   327    phone: clinic?.phone || '',
   328    address: clinic?.address || ''
   329  });
   330  const [loading, setLoading] = useState(false);
   331  const [originalData, setOriginalData] = useState({});
   332
   333  useEffect(() => {
   334    if (clinic) {
   335      console.log('CLINIC: Loading clinic data into form:', clinic);
   336      const clinicData = {
   337        name: clinic.name || '',
   338        contactPerson: clinic.contactPerson || clinic.contact_person || '',
   339        email: clinic.email || '',
   340        phone: clinic.phone || '',
   341        address: clinic.address || ''
   342      };
   343      console.log('NOTE: Form data populated:', clinicData);
   344      setFormData(clinicData);
   345      setOriginalData(clinicData); // Store original data for change tracking
   346    }
   347  }, [clinic]);
   348
   349  const handleInputChange = (field, value) => {
   350    setFormData(prev => ({
   351      ...prev,
   352      [field]: value
   353    }));
   354  };
   355
   356  const getChangedFields = () => {
   357    const changes = {};
   358    Object.keys(formData).forEach(key => {
   359      if (formData[key] !== originalData[key]) {
   360        changes[key] = {
   361          old: originalData[key],
   362          new: formData[key]
   363        };
   364      }
   365    });
   366    return changes;
   367  };
   368
   369  const createProfileChangeAlert = async (changes) => {
   370    try {
   371      const changesList = Object.keys(changes).map(field => {
   372        const fieldNames = {
   373          name: 'Clinic Name',
   374          contactPerson: 'Contact Person',
   375          email: 'Email',
   376          phone: 'Phone',
   377          address: 'Address'
   378        };
   379        
   380        return `${fieldNames[field]}: "${changes[field].old}"  "${changes[field].new}"`;
   381      }).join('\n');
   382
   383      const alert = {
   384        id: `alert_${Date.now()}`,
   385        type: 'profile_change',
   386        severity: 'info',
   387        title: `Profile Updated - ${clinic?.name}`,
   388        message: `Clinic "${clinic?.name}" has updated their profile information:\n\n${changesList}`,
   389        clinicId: clinic?.id,
   390        clinicName: clinic?.name,
   391        changes: changes,
   392        createdAt: new Date().toISOString(),
   393        read: false,
   394        actionRequired: false
   395      };
   396
   397      // Add alert to database
   398      await DatabaseService.add('alerts', alert);
   399      console.log('SUCCESS: Profile change alert created for super admin');
   400      
   401      return true;
   402    } catch (error) {
   403      console.error('ERROR: Failed to create profile change alert:', error);
   404      return false;
   405    }
   406  };
   407
   408  const handleSaveChanges = async () => {
   409    if (!clinic?.id) {
   410      toast.error('Clinic ID not found');
   411      return;
   412    }
   413
   414    setLoading(true);
   415    try {
   416      // Get changed fields
   417      const changes = getChangedFields();
   418      
   419      if (Object.keys(changes).length === 0) {
   420        toast.info('No changes to save');
   421        setLoading(false);
   422        return;
   423      }
   424
   425      // Update clinic profile in database
   426      await DatabaseService.update('clinics', clinic.id, formData);
   427      
   428      // Create alert for super admin
   429      const alertCreated = await createProfileChangeAlert(changes);
   430      
   431      // Update original data to reflect saved state
   432      setOriginalData({ ...formData });
   433      
   434      // Success message
   435      if (alertCreated) {
   436        toast.success('Profile updated successfully! Super Admin has been notified of the changes.');
   437      } else {
   438        toast.success('Profile updated successfully!');
   439      }
   440      
   441    } catch (error) {
   442      console.error('Error updating profile:', error);
   443      toast.error('Failed to update profile. Please try again.');
   444    } finally {
   445      setLoading(false);
   446    }
   447  };
   448
   449  const hasChanges = Object.keys(getChangedFields()).length > 0;
   450
   451  return (
   452    <div className="space-y-6">
   453      <div className="bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700 p-6">
   454        <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Clinic Information</h3>
   455
   456        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
   457          <div>
   458            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Clinic Name</label>
   459            <input
   460              type="text"
   461              value={formData.name}
   462              onChange={(e) => handleInputChange('name', e.target.value)}
   463              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:ring-1 focus:ring-primary-500 focus:border-primary-500"
   464              placeholder="Enter clinic name"
   465            />
   466          </div>
   467
   468          <div>
   469            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Contact Person</label>
   470            <input
   471              type="text"
   472              value={formData.contactPerson}
   473              onChange={(e) => handleInputChange('contactPerson', e.target.value)}
   474              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:ring-1 focus:ring-primary-500 focus:border-primary-500"
   475              placeholder="Enter contact person name"
   476            />
