   506            onChange={(e) => handleInputChange('address', e.target.value)}
   507            rows={3}
   508            className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:ring-1 focus:ring-primary-500 focus:border-primary-500"
   509            placeholder="Enter clinic address"
   510          />
   511        </div>
   512
   513        {hasChanges && (
   514          <div className="mt-4 p-3 bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-md">
   515            <p className="text-sm text-yellow-800 dark:text-yellow-400">
   516              <strong>Pending Changes:</strong> You have unsaved changes. Click "Save Changes" to update your profile.
   517            </p>
   518          </div>
