     1â†’import { v4 as uuidv4 } from 'uuid';
     2â†’import SupabaseService from './supabaseService';
     3â†’
     4â†’// Enhanced database service - uses Supabase only, no localStorage fallback
     5â†’class DatabaseService {
     6â†’  constructor() {
     7â†’    this.useSupabase = true;
     8â†’    this.supabaseService = SupabaseService;
     9â†’    this.checkSupabaseAvailability();
    10â†’  }
    11â†’
    12â†’  async checkSupabaseAvailability() {
    13â†’    try {
    14â†’      console.log('CONFIG: Checking Supabase availability...');
    15â†’
    16â†’      // Test Supabase connection with clinics table (which we know exists)
    17â†’      const testResult = await this.supabaseService.get('clinics');
    18â†’      if (testResult !== undefined) {
    19â†’        this.useSupabase = true;
    20â†’        console.log('START: Using Supabase for data storage');
    21â†’      } else {
    22â†’        throw new Error('Supabase connection failed');
    23â†’      }
    24â†’    } catch (error) {
    25â†’      console.error('ERROR: Supabase connection failed:', error);
    26â†’      throw new Error('Database connection required. Please check your internet connection and try again.');
    27â†’    }
    28â†’  }
    29â†’
    30â†’  // Map legacy table names to Supabase schema
    31â†’  mapTableName(table) {
    32â†’    const tableMapping = {
    33â†’      'clinics': 'clinics',              // Use existing clinics table
    34â†’      'superAdmins': 'profiles',
    35â†’      'patients': 'patients',
    36â†’      'reports': 'reports',              // Fixed: Use 'reports' not 'eeg_reports'
    37â†’      'subscriptions': 'subscriptions',
    38â†’      'payments': 'payment_history',
    39â†’      'algorithmResults': 'algorithm_results',  // QEEG algorithm processing results
    40â†’      'usage': 'organizations',          // Temporary mapping to existing table
    41â†’      'alerts': 'organizations'          // Temporary mapping to existing table
    42â†’    };
    43â†’
    44â†’    return tableMapping[table] || table;
    45â†’  }
    46â†’
    47â†’  // Generic CRUD operations
    48â†’  async get(table) {
    49â†’    try {
    50â†’      const actualTable = this.mapTableName(table);
    51â†’      const data = await this.supabaseService.get(actualTable);
    52â†’      console.log(`DATA: ${table} from Supabase (${actualTable}):`, data?.length || 0, 'items');
    53â†’
    54â†’      // Ensure data is always an array
    55â†’      if (!data) {
    56â†’        console.warn(`WARNING: No data returned for ${table}, returning empty array`);
    57â†’        return [];
    58â†’      }
    59â†’
    60â†’      if (!Array.isArray(data)) {
    61â†’        console.warn(`WARNING: Data for ${table} is not an array:`, typeof data);
    62â†’        return [];
    63â†’      }
    64â†’
    65â†’      // Transform data based on table type
    66â†’      if (table === 'clinics' && actualTable === 'clinics') {
    67â†’        // Transform clinics data to camelCase format
    68â†’        return data.map(clinic => ({
    69â†’          id: clinic.id,
    70â†’          name: clinic.name,
    71â†’          email: clinic.email,
    72â†’          password: clinic.password,  // SUCCESS: CRITICAL: Include password for login authentication
    73â†’          contactPerson: clinic.contact_person,
    74â†’          contact_person: clinic.contact_person,  // Keep snake_case for compatibility
    75â†’          clinicName: clinic.clinic_name,
    76â†’          clinic_name: clinic.clinic_name,  // Keep snake_case for compatibility
    77â†’          phone: clinic.phone,
    78â†’          address: clinic.address,
    79â†’          logoUrl: clinic.logo_url,
    80â†’          logo_url: clinic.logo_url,  // Keep snake_case for compatibility
    81â†’          avatar: clinic.logo_url,  // Map logo_url to avatar
    82â†’          isActive: clinic.is_active,
    83â†’          is_active: clinic.is_active,  // Keep snake_case for compatibility
    84â†’          isActivated: clinic.is_active,  // Legacy compatibility
    85â†’          reportsUsed: clinic.reports_used,
    86â†’          reportsAllowed: clinic.reports_allowed,
    87â†’          subscriptionStatus: clinic.subscription_status,
    88â†’          subscription_status: clinic.subscription_status,  // Keep snake_case for compatibility
    89â†’          subscriptionTier: clinic.subscription_tier,
    90â†’          trialStartDate: clinic.trial_start_date,
    91â†’          trialEndDate: clinic.trial_end_date,
    92â†’          createdAt: clinic.created_at,
    93â†’          updatedAt: clinic.updated_at
    94â†’        }));
    95â†’      }
    96â†’
    97â†’      if (table === 'patients' && actualTable === 'patients') {
    98â†’        // Extra safety check for patients data
    99â†’        if (!data || !Array.isArray(data) || data.length === 0) {
   100â†’          console.warn('WARNING: No patients data to transform, returning empty array');
   101â†’          console.warn('WARNING: Data value:', data);
   102â†’          console.warn('WARNING: Is array:', Array.isArray(data));
   103â†’          console.warn('WARNING: Length:', data?.length);
   104â†’          return [];
   105â†’        }
   106â†’
   107â†’        console.log('SUCCESS: Transforming patients data, count:', data.length);
   108â†’        console.log('SUCCESS: Raw patients data before transform:', data);
   109â†’
   110â†’        // Transform patients data to camelCase format
   111â†’        const transformed = data.map(patient => {
   112â†’          if (!patient) {
   113â†’            console.warn('WARNING: Null patient in data array, skipping');
   114â†’            return null;
   115â†’          }
   116â†’
   117â†’          console.log('REFRESH: Transforming patient:', patient.email);
   118â†’
   119â†’          return {
   120â†’            id: patient.id,
   121â†’            name: patient.name,
   122â†’            fullName: patient.full_name || patient.name,
   123â†’            full_name: patient.full_name || patient.name,  // Keep snake_case for compatibility
   124â†’            email: patient.email,
   125â†’            phone: patient.phone,
   126â†’            address: patient.address,
   127â†’            dateOfBirth: patient.date_of_birth,
   128â†’            date_of_birth: patient.date_of_birth,  // Keep snake_case for compatibility
   129â†’            gender: patient.gender,
   130â†’            clinicId: patient.clinic_id || patient.org_id,
   131â†’            clinic_id: patient.clinic_id || patient.org_id,  // Keep snake_case for compatibility
   132â†’            orgId: patient.org_id || patient.clinic_id,
   133â†’            org_id: patient.org_id || patient.clinic_id,  // Keep snake_case for compatibility
   134â†’            medicalHistory: patient.medical_history,
   135â†’            medical_history: patient.medical_history,  // Keep snake_case for compatibility
   136â†’            emergencyContact: patient.emergency_contact,
   137â†’            emergency_contact: patient.emergency_contact,  // Keep snake_case for compatibility
   138â†’            improvementFocus: patient.improvement_focus,
   139â†’            brainFitnessScore: patient.brain_fitness_score,
   140â†’            createdAt: patient.created_at,
   141â†’            updatedAt: patient.updated_at
   142â†’          };
   143â†’        }).filter(p => p !== null);
   144â†’
   145â†’        console.log('SUCCESS: Transformed patients data, count:', transformed.length);
   146â†’        console.log('SUCCESS: Transformed patients:', transformed);
   147â†’        return transformed;
   148â†’      }
   149â†’
   150â†’      const result = this.convertToCamelCase(data);
   151â†’
   152â†’      // Final safety check - ensure result is always an array
   153â†’      return Array.isArray(result) ? result : [];
   154â†’    } catch (error) {
   155â†’      console.error(`ERROR: Failed to get data from ${table}:`, error);
   156â†’      // Return empty array instead of throwing to prevent crashes
   157â†’      return [];
   158â†’    }
   159â†’  }
