   161â†’  async add(table, item) {
   162â†’    try {
   163â†’      const actualTable = this.mapTableName(table);
   164â†’
   165â†’      // Handle clinic creation specially
   166â†’      if (table === 'clinics') {
   167â†’        return await this.createClinic(item);
   168â†’      }
   169â†’
   170â†’      // Ensure item has an ID
   171â†’      if (!item.id) {
   172â†’        item.id = uuidv4();
   173â†’      }
   174â†’
   175â†’      // Filter valid fields based on table
   176â†’      const filteredItem = this.filterValidFields(actualTable, item);
   177â†’
   178â†’      // Convert field names to snake_case for Supabase
   179â†’      const supabaseItem = this.convertToSnakeCase(filteredItem);
   180â†’      const result = await this.supabaseService.add(actualTable, supabaseItem);
   181â†’      console.log(`DATA: Added to Supabase ${table}:`, item.name || item.id);
   182â†’
   183â†’      return this.convertToCamelCase(result);
   184â†’    } catch (error) {
   185â†’      console.error(`ERROR: Failed to add to ${table}:`, error);
   186â†’      throw error;
   187â†’    }
   188â†’  }
   189â†’
   190â†’  // Filter valid fields for each table
   191â†’  filterValidFields(table, item) {
   192â†’    const validFields = {
   193â†’      'clinics': [
   194â†’        'id', 'name', 'clinic_name', 'email', 'contact_person', 'phone', 'address', 'logo_url', 'is_active',
   195â†’        'reports_used', 'reports_allowed', 'subscription_status', 'subscription_tier',
   196â†’        'trial_start_date', 'trial_end_date', 'created_at', 'updated_at',
   197â†’        'password' // ONLY use password field for authentication
   198â†’        // Note: avatar stored in logo_url field
   199â†’      ],
   200â†’      'organizations': [
   201â†’        'id', 'name', 'description', 'website', 'logo_url', 'is_active',
   202â†’        'created_at', 'updated_at', 'owner_user_id'
   203â†’      ],
   204â†’      'profiles': [
   205â†’        'id', 'role', 'full_name', 'phone', 'avatar_url', 'created_at', 'updated_at'
   206â†’      ],
   207â†’      'org_memberships': [
   208â†’        'org_id', 'user_id', 'role', 'created_at'
   209â†’      ],
   210â†’      'patients': [
   211â†’        'id', 'org_id', 'clinic_id', 'owner_user', 'external_id', 'name', 'full_name', 'date_of_birth',
   212â†’        'gender', 'phone', 'email', 'address', 'medical_history', 'improvement_focus',
   213â†’        'brain_fitness_score', 'emergency_contact', 'created_at', 'updated_at',
   214â†’        'avatar', 'profile_image', 'profileImage', 'avatar_url'
   215â†’      ],
   216â†’      'reports': [
   217â†’        // Actual schema from 004_simple_clinic_tables.sql:
   218â†’        'id', 'clinic_id', 'patient_id', 'file_name', 'file_path',
   219â†’        'report_data', 'status', 'created_at', 'updated_at'
   220â†’        // Note: report_type, file_size, etc. should be stored in report_data JSONB
   221â†’      ],
   222â†’      'payment_history': [
   223â†’        'id', 'payment_id', 'order_id', 'signature', 'clinic_id', 'amount', 'currency', 'status',
   224â†’        'package_id', 'package_name', 'reports', 'plan_details', 'subscription', 'payment_details',
   225â†’        'provider', 'ip_address', 'user_agent', 'metadata', 'created_at', 'updated_at'
   226â†’      ],
   227â†’      'subscriptions': [
   228â†’        'id', 'clinic_id', 'plan', 'status', 'amount', 'currency', 'package_name', 'payment_method',
   229â†’        'payment_id', 'reports_allowed', 'environment', 'plan_details', 'subscription', 'payment_details',
   230â†’        'created_at', 'updated_at'
   231â†’      ],
   232â†’      'algorithm_results': [
   233â†’        'id', 'patient_id', 'patient_name', 'clinic_id', 'clinic_name', 'results',
   234â†’        'eyes_open_file', 'eyes_closed_file', 'processed_at', 'processed_by',
   235â†’        'pdf_url', 'created_at'
   236â†’      ]
   237â†’    };
   238â†’
   239â†’    const allowedFields = validFields[table];
   240â†’    if (!allowedFields) {
   241â†’      return item; // No filtering if table not defined
   242â†’    }
   243â†’
   244â†’    const filteredItem = {};
   245â†’    for (const [key, value] of Object.entries(item)) {
   246â†’      const snakeKey = this.toSnakeCase(key);
   247â†’      if (allowedFields.includes(snakeKey) || allowedFields.includes(key)) {
   248â†’        filteredItem[key] = value;
   249â†’      } else {
   250â†’        console.log(` Filtering out invalid field for ${table}: ${key}`);
   251â†’      }
   252â†’    }
   253â†’
   254â†’    return filteredItem;
   255â†’  }
