   257â†’  async update(table, id, updates) {
   258â†’    try {
   259â†’      const actualTable = this.mapTableName(table);
   260â†’      console.log(`REFRESH: DatabaseService.update - Table: ${table}, ID: ${id}`);
   261â†’      console.log(`REFRESH: Original updates:`, updates);
   262â†’
   263â†’      // Filter valid fields based on table
   264â†’      const filteredUpdates = this.filterValidFields(actualTable, updates);
   265â†’      console.log(`REFRESH: Filtered updates:`, filteredUpdates);
   266â†’
   267â†’      const supabaseUpdates = this.convertToSnakeCase(filteredUpdates);
   268â†’      console.log(`REFRESH: Snake_case updates for Supabase:`, supabaseUpdates);
   269â†’
   270â†’      const result = await this.supabaseService.update(actualTable, id, supabaseUpdates);
   271â†’      console.log(`DATA: Updated in Supabase ${table}:`, id);
