   310â†’  async findBy(table, field, value) {
   311â†’    try {
   312â†’      const actualTable = this.mapTableName(table);
   313â†’      const snakeField = this.toSnakeCase(field);
   314â†’      const results = await this.supabaseService.findBy(actualTable, snakeField, value);
   315â†’      return results.map(item => this.convertToCamelCase(item));
   316â†’    } catch (error) {
