   322â†’  async findOne(table, field, value) {
   323â†’    try {
   324â†’      const actualTable = this.mapTableName(table);
   325â†’      const snakeField = this.toSnakeCase(field);
   326â†’      const result = await this.supabaseService.findOne(actualTable, snakeField, value);
