   334â†’  // Case-insensitive search by field
   335â†’  async findByNameIgnoreCase(table, name) {
   336â†’    try {
   337â†’      const actualTable = this.mapTableName(table);
   338â†’      const results = await this.supabaseService.findByNameIgnoreCase(actualTable, name);
   339â†’      return results.map(item => this.convertToCamelCase(item));
