   346â†’  // Convert between camelCase and snake_case
   347â†’  toSnakeCase(str) {
   348â†’    return str.replace(/[A-Z]/g, letter => `_${letter.toLowerCase()}`);
   349â†’  }
   350â†’
   351â†’  toCamelCase(str) {
   352â†’    return str.replace(/_([a-z])/g, (_, letter) => letter.toUpperCase());
   353â†’  }
   354â†’
   355â†’  convertToSnakeCase(obj) {
   356â†’    if (!obj || typeof obj !== 'object') return obj;
   357â†’    if (Array.isArray(obj)) return obj.map(item => this.convertToSnakeCase(item));
   358â†’
   359â†’    const converted = {};
   360â†’    for (const [key, value] of Object.entries(obj)) {
   361â†’      const snakeKey = this.toSnakeCase(key);
   362â†’      converted[snakeKey] = value;
   363â†’    }
   364â†’    return converted;
   365â†’  }
   366â†’
   367â†’  convertToCamelCase(obj) {
   368â†’    if (!obj || typeof obj !== 'object') return obj;
   369â†’    if (Array.isArray(obj)) return obj.map(item => this.convertToCamelCase(item));
   370â†’
   371â†’    const converted = {};
   372â†’    for (const [key, value] of Object.entries(obj)) {
   373â†’      const camelKey = this.toCamelCase(key);
   374â†’      converted[camelKey] = value;
   375â†’    }
   376â†’    return converted;
   377â†’  }
