   379â†’  // Super Admin specific methods
   380â†’  async authenticateAdmin(email, password) {
   381â†’    // Check localStorage for super admin (development mode)
   382â†’    const superAdmins = await this.get('superAdmins');
   383â†’    const admin = superAdmins.find(a => a.email === email);
   384â†’    if (admin && admin.password === password && admin.isActive) {
   385â†’      return { ...admin, password: undefined };
   386â†’    }
   387â†’
   388â†’    // Try Supabase authentication
   389â†’    if (this.useSupabase) {
   390â†’      try {
   391â†’        const result = await this.supabaseService.signIn(email, password);
   392â†’        if (result?.user) {
   393â†’          return {
   394â†’            id: result.user.id,
   395â†’            email: result.user.email,
   396â†’            role: result.user.user_metadata?.role || 'user',
   397â†’            name: result.user.user_metadata?.name || 'User'
   398â†’          };
   399â†’        }
   400â†’      } catch (error) {
   401â†’        console.error('Supabase auth failed:', error);
   402â†’      }
   403â†’    }
   404â†’
   405â†’    return null;
   406â†’  }
