   444â†’          console.log('SUCCESS: Patient profile created');
   445â†’        } catch (profileError) {
   446â†’          console.error('WARNING: Profile creation failed (continuing anyway):', profileError);
   447â†’        }
   448â†’      }
   449â†’
   450â†’      return data;
   451â†’    } catch (error) {
   452â†’      console.error('ERROR: Failed to create patient auth:', error);
   453â†’      throw error;
