   457â†’  // Clinic specific methods
   458â†’  async createClinic(clinicData) {
   459â†’    try {
   460â†’      console.log('CLINIC: Creating clinic with data:', clinicData);
   461â†’
   462â†’      // Create clinic record matching the exact schema
   463â†’      // Preserve the data passed from authService (including pending approval status)
   464â†’      const clinicRecord = {
   465â†’        name: clinicData.name || clinicData.clinicName,
   466â†’        email: clinicData.email,
   467â†’        phone: clinicData.phone || '',
   468â†’        address: clinicData.address || '',
   469â†’        logo_url: clinicData.logo_url || clinicData.logoUrl || null,
   470â†’        is_active: clinicData.is_active !== undefined ? clinicData.is_active : true,
   471â†’        reports_used: clinicData.reports_used || clinicData.reportsUsed || 0,
   472â†’        reports_allowed: clinicData.reports_allowed || parseInt(clinicData.reportsAllowed) || 10,
   473â†’        subscription_status: clinicData.subscription_status || clinicData.subscriptionStatus || 'trial',
   474â†’        subscription_tier: clinicData.subscription_tier || 'free',
   475â†’        trial_start_date: clinicData.trial_start_date || new Date().toISOString(),
   476â†’        trial_end_date: clinicData.trial_end_date || new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString(),
   477â†’        created_at: clinicData.created_at || clinicData.createdAt || new Date().toISOString(),
   478â†’        updated_at: clinicData.updated_at || new Date().toISOString()
   479â†’      };
   480â†’
   481â†’      // IMPORTANT: Preserve the ID if provided (for existing clinic records)
   482â†’      if (clinicData.id) {
   483â†’        clinicRecord.id = clinicData.id;
   484â†’      }
   485â†’
   486â†’      console.log('INFO: Clinic data to create:', clinicRecord);
   487â†’
   488â†’      // Use direct Supabase insert to clinics table
