import React, { useState, useEffect } from 'react';
import { Upload, Play, Download, FileText, CheckCircle, Activity, User, Building2, Calendar, History, X, ArrowLeft, Search, Filter } from 'lucide-react';
import DatabaseService from '../../services/databaseService';
import toast from 'react-hot-toast';

const AlgorithmDataProcessor = () => {
  const [patients, setPatients] = useState([]);
  const [clinics, setClinics] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [showProcessingUI, setShowProcessingUI] = useState(false);
  const [processingHistory, setProcessingHistory] = useState([]);
  const [loading, setLoading] = useState(true);

  // Filter states
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedClinicFilter, setSelectedClinicFilter] = useState('');
  const [dateFilter, setDateFilter] = useState('');

  const [eyesOpenFile, setEyesOpenFile] = useState(null);
  const [eyesClosedFile, setEyesClosedFile] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingComplete, setProcessingComplete] = useState(false);
  const [consoleLog, setConsoleLog] = useState([]);
  const [progress, setProgress] = useState(0);
  const [results, setResults] = useState(null);
  const [isSaved, setIsSaved] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [pdfUrl, setPdfUrl] = useState(null);

  // Debug function to check database contents
  useEffect(() => {
    window.debugAlgorithmResults = async () => {
      const allResults = await DatabaseService.get('algorithmResults');
      console.log('ðŸ” All Algorithm Results in Database:', allResults);
      console.log(`Total records: ${allResults.length}`);
      allResults.forEach((record, idx) => {
        console.log(`\nðŸ“Š Record ${idx + 1}:`, {
          id: record.id,
          patientName: record.patientName,
          processedAt: record.processedAt,
          pdfUrl: record.pdfUrl || 'âŒ MISSING',
          hasResults: !!record.results
        });
      });
    };
    console.log('ðŸ’¡ Debug: Type window.debugAlgorithmResults() in console to check database');
  }, []);

  useEffect(() => {
    loadPatients();
  }, []);

  useEffect(() => {
    if (selectedPatient && showProcessingUI) {
      loadProcessingHistory(selectedPatient.id);
    }
  }, [selectedPatient, showProcessingUI, isSaved]); // Re-load when saved

  const loadPatients = async () => {
    try {
      const patientsData = await DatabaseService.get('patients');
      const clinicsData = await DatabaseService.get('clinics');
      const algorithmResults = await DatabaseService.get('algorithmResults') || [];

      // Enrich patients with clinic names and algorithm status
      const enrichedPatients = patientsData.map(patient => {
        const patientResults = algorithmResults.filter(r => r.patientId === patient.id);
        const lastResult = patientResults.length > 0
          ? patientResults.sort((a, b) => new Date(b.processedAt) - new Date(a.processedAt))[0]
          : null;

        return {
          ...patient,
          clinicName: clinicsData.find(c => c.id === patient.clinicId)?.name || 'Unknown Clinic',
          algorithmStatus: lastResult ? 'completed' : 'pending',
          lastProcessed: lastResult?.processedAt,
          totalScans: patientResults.length
        };
      });

      setPatients(enrichedPatients);
      setClinics(clinicsData);
      setLoading(false);
    } catch (error) {
      console.error('Error loading patients:', error);
      toast.error('Failed to load patients');
      setLoading(false);
    }
  };

  const loadProcessingHistory = async (patientId) => {
    try {
      // Load algorithm results for this patient
      const results = await DatabaseService.get('algorithmResults');
      const patientResults = results.filter(r => r.patientId === patientId);

      console.log(`ðŸ“‹ Loaded ${patientResults.length} records for patient ${patientId}`);

      // Log all records for debugging
      console.log('ðŸ“Š All records for this patient:');
      patientResults.forEach((rec, idx) => {
        console.log(`   ${idx + 1}. ${new Date(rec.processedAt).toLocaleString()} - PDF: ${rec.pdfUrl ? 'âœ“' : 'âœ—'}`);
      });

      // Check for records without pdfUrl and try to fix them
      const recordsWithoutPdf = patientResults.filter(r => !r.pdfUrl);
      if (recordsWithoutPdf.length > 0) {
        console.log(`\nâš ï¸ Found ${recordsWithoutPdf.length} records without PDF URL - attempting auto-fix...`);

        let updatedCount = 0;

        // Step 1: Try to fetch PDFs from Supabase bucket
        try {
          const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
          const response = await fetch(`${apiUrl}/qeeg/supabase-pdfs`);

          if (response.ok) {
            const data = await response.json();
            console.log(`\nâ˜ï¸ Found ${data.count} PDFs in Supabase bucket:`);
            data.pdfs.forEach(pdf => console.log(`   - ${pdf.name}`));

            // Match PDFs with database records using smart matching
            console.log(`\nðŸ” Attempting to match ${recordsWithoutPdf.length} records...`);
            for (const record of recordsWithoutPdf) {
              const recordTimestamp = new Date(record.processedAt).getTime();
              const sanitizedName = record.patientName.replace(/[^a-z0-9]/gi, '_').toLowerCase();

              console.log(`\n   Record: ${record.patientName} @ ${new Date(record.processedAt).toLocaleString()}`);
              console.log(`   Searching for PDFs matching: neurosense-report-${sanitizedName}-*.pdf`);

              // Find PDFs matching patient name
              const matchingPdfs = data.pdfs.filter(pdf => {
                const pdfNameLower = pdf.name.toLowerCase();
                return pdfNameLower.includes(`neurosense-report-${sanitizedName}-`) &&
                       pdfNameLower.endsWith('.pdf');
              });

              console.log(`   Found ${matchingPdfs.length} PDFs for this patient`);

              if (matchingPdfs.length > 0) {
                // Find PDF with closest timestamp (within 60 seconds)
                let bestMatch = null;
                let smallestDiff = Infinity;

                for (const pdf of matchingPdfs) {
                  // Extract timestamp from filename: neurosense-report-name-TIMESTAMP.pdf
                  const match = pdf.name.match(/-(\d+)\.pdf$/);
                  if (match) {
                    const pdfTimestamp = parseInt(match[1]);
                    const diff = Math.abs(pdfTimestamp - recordTimestamp);
                    console.log(`      ${pdf.name}: timestamp diff = ${diff}ms (${(diff/1000).toFixed(1)}s)`);

                    // Accept if within 60 seconds (60000ms)
                    if (diff < 60000 && diff < smallestDiff) {
                      smallestDiff = diff;
                      bestMatch = pdf;
                    }
                  }
                }

                if (bestMatch) {
                  console.log(`   âœ… MATCH FOUND! ${bestMatch.name} (${(smallestDiff/1000).toFixed(1)}s difference)`);
                  console.log(`   URL: ${bestMatch.url}`);
                  record.pdfUrl = bestMatch.url;
                  await DatabaseService.update('algorithmResults', record.id, record);
                  updatedCount++;
                } else {
                  console.log(`   âš ï¸ PDFs found but timestamp difference too large (>60s)`);
                }
              } else {
                console.log(`   âŒ No PDFs found for patient: ${sanitizedName}`);
              }
            }
          }
        } catch (supabaseErr) {
          console.log('âš ï¸ Could not fetch from Supabase, trying local storage...');
        }

        // Step 2: If Supabase fails, try local storage
        for (const record of recordsWithoutPdf) {
          if (record.pdfUrl) continue; // Already updated from Supabase

          try {
            const timestamp = new Date(record.processedAt).getTime();
            const sanitizedName = record.patientName.replace(/[^a-z0-9]/gi, '_').toLowerCase();
            const possibleFilename = `neurosense-report-${sanitizedName}-${timestamp}.pdf`;
            const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
            const backendBaseUrl = apiUrl.replace('/api', '');
            const testUrl = `${backendBaseUrl}/uploads/${possibleFilename}`;

            // Test if file exists locally
            const response = await fetch(testUrl, { method: 'HEAD' });
            if (response.ok) {
              console.log(`âœ… Found local PDF for record ${record.id}: ${testUrl}`);
              record.pdfUrl = `/uploads/${possibleFilename}`;
              await DatabaseService.update('algorithmResults', record.id, record);
              updatedCount++;
            }
          } catch (err) {
            console.log(`âŒ Could not find PDF for record ${record.id}`);
          }
        }

        if (updatedCount > 0) {
          console.log(`âœ… Auto-fixed ${updatedCount} records with PDF URLs`);
          // Reload the results after updating
          const updatedResults = await DatabaseService.get('algorithmResults');
          const updatedPatientResults = updatedResults.filter(r => r.patientId === patientId);
          setProcessingHistory(updatedPatientResults);

          updatedPatientResults.forEach((record, idx) => {
            console.log(`   Record ${idx + 1}:`, {
              processedAt: record.processedAt,
              hasPdfUrl: !!record.pdfUrl,
              pdfUrl: record.pdfUrl || 'NOT SET',
              pdfType: record.pdfUrl ? (record.pdfUrl.startsWith('http') ? 'Supabase' : 'Local') : 'None'
            });
          });
          return;
        }
      }

      patientResults.forEach((record, idx) => {
        console.log(`   Record ${idx + 1}:`, {
          processedAt: record.processedAt,
          hasPdfUrl: !!record.pdfUrl,
          pdfUrl: record.pdfUrl || 'NOT SET',
          pdfType: record.pdfUrl ? (record.pdfUrl.startsWith('http') ? 'Supabase' : 'Local') : 'None'
        });
      });

      setProcessingHistory(patientResults);
    } catch (error) {
      console.error('Error loading processing history:', error);
      setProcessingHistory([]);
    }
  };

  const handleGenerateReport = (patient) => {
    setSelectedPatient(patient);
    setShowProcessingUI(true);
    // Reset upload states
    setEyesOpenFile(null);
    setEyesClosedFile(null);
    setResults(null);
    setProcessingComplete(false);
    setConsoleLog([]);
    setIsSaved(false);
    setPdfUrl(null);
    setIsSaving(false);
  };

  const handleBackToList = () => {
    setShowProcessingUI(false);
    setSelectedPatient(null);
    setEyesOpenFile(null);
    setEyesClosedFile(null);
    setResults(null);
    setProcessingComplete(false);
    setConsoleLog([]);
    setProcessingHistory([]);
    setIsSaved(false);
    setIsSaving(false);
    // Reload patients to update status
    loadPatients();
  };

  // Helper function to get patient name (handles different field names)
  const getPatientName = (patient) => {
    return patient?.fullName || patient?.full_name || patient?.name || patient?.email || 'Unknown Patient';
  };

  // Helper function to get status badge
  const getStatusBadge = (status, lastProcessed) => {
    if (status === 'completed') {
      return (
        <div className="flex flex-col items-start">
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-400">
            <CheckCircle className="h-3 w-3 mr-1" />
            Completed
          </span>
          {lastProcessed && (
            <span className="text-xs text-gray-500 dark:text-gray-400 mt-1">
              {new Date(lastProcessed).toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}
            </span>
          )}
        </div>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900/30 dark:text-yellow-400">
        <Activity className="h-3 w-3 mr-1" />
        Pending
      </span>
    );
  };

  // Filter patients based on search and filters
  const filteredPatients = patients.filter(patient => {
    const matchesSearch = searchTerm === '' ||
      getPatientName(patient).toLowerCase().includes(searchTerm.toLowerCase()) ||
      patient.email?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesClinic = selectedClinicFilter === '' || patient.clinicId === selectedClinicFilter;

    const matchesDate = dateFilter === '' ||
      (patient.lastProcessed && new Date(patient.lastProcessed).toISOString().split('T')[0] === dateFilter);

    return matchesSearch && matchesClinic && matchesDate;
  });

  // Group filtered patients by clinic
  const groupedPatients = clinics.map(clinic => ({
    clinic,
    patients: filteredPatients.filter(p => p.clinicId === clinic.id)
  })).filter(group => group.patients.length > 0);

  const clearFilters = () => {
    setSearchTerm('');
    setSelectedClinicFilter('');
    setDateFilter('');
  };

  const handleFileUpload = (type, event) => {
    const file = event.target.files[0];
    if (file) {
      if (type === 'eo') {
        setEyesOpenFile(file);
      } else {
        setEyesClosedFile(file);
      }
      // Reset processing state when new files are uploaded
      setProcessingComplete(false);
      setResults(null);
      setConsoleLog([]);
      setProgress(0);
      setIsSaved(false);
      setIsSaving(false);
      setPdfUrl(null);
    }
  };

  const processQEEGFiles = async () => {
    setIsProcessing(true);
    setConsoleLog([]);
    setProgress(0);

    try {
      // Console log: Starting process
      setConsoleLog(prev => [...prev, 'ðŸš€ Starting QEEG analysis...']);
      setProgress(10);

      // Create FormData to send files
      const formData = new FormData();
      formData.append('eyesOpen', eyesOpenFile);
      formData.append('eyesClosed', eyesClosedFile);
      formData.append('patientId', selectedPatient.id);
      formData.append('patientName', getPatientName(selectedPatient));
      formData.append('clinicName', selectedPatient.clinicName);

      setConsoleLog(prev => [...prev, 'ðŸ“¤ Uploading files to server...']);
      setProgress(20);

      // Call backend API
      const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
      const response = await fetch(`${apiUrl}/qeeg/process`, {
        method: 'POST',
        body: formData
      });

      setConsoleLog(prev => [...prev, 'ðŸ“Š Parsing QEEG data tables...']);
      setProgress(40);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to process QEEG files');
      }

      const data = await response.json();

      // Auto-set PDF URL if returned from backend
      if (data.data.pdfUrl) {
        setPdfUrl(data.data.pdfUrl);
        console.log('âœ… PDF auto-generated:', data.data.pdfUrl);
        setConsoleLog(prev => [...prev, 'ðŸ“„ PDF report generated automatically']);
      }

      setConsoleLog(prev => [...prev, 'ðŸ§® Calculating 7 brain health parameters...']);
      setProgress(60);

      // Simulate calculation steps for better UX
      await new Promise(resolve => setTimeout(resolve, 500));
      setConsoleLog(prev => [...prev, '  âœ“ Cognition parameter calculated']);
      setProgress(65);

      await new Promise(resolve => setTimeout(resolve, 400));
      setConsoleLog(prev => [...prev, '  âœ“ Stress parameter calculated']);
      setProgress(70);

      await new Promise(resolve => setTimeout(resolve, 400));
      setConsoleLog(prev => [...prev, '  âœ“ Focus & Attention parameter calculated']);
      setProgress(75);

      await new Promise(resolve => setTimeout(resolve, 400));
      setConsoleLog(prev => [...prev, '  âœ“ Burnout & Fatigue parameter calculated']);
      setProgress(80);

      await new Promise(resolve => setTimeout(resolve, 400));
      setConsoleLog(prev => [...prev, '  âœ“ Emotional Regulation parameter calculated']);
      setProgress(85);

      await new Promise(resolve => setTimeout(resolve, 400));
      setConsoleLog(prev => [...prev, '  âœ“ Learning parameter calculated']);
      setProgress(90);

      await new Promise(resolve => setTimeout(resolve, 400));
      setConsoleLog(prev => [...prev, '  âœ“ Creativity parameter calculated']);
      setProgress(95);

      // Convert API results to display format
      const finalResults = data.data.results.map(param => ({
        parameter: param.name,
        score: Math.round((param.score / param.maxScore) * 100), // Convert to percentage
        rawScore: `${param.score}/${param.maxScore}`,
        status: param.classification,
        color: param.classification === 'High' ? 'green' :
               param.classification === 'Medium' ? 'blue' : 'orange',
        metrics: param.metrics
      }));

      setConsoleLog(prev => [...prev, 'âœ… Analysis complete!']);
      setConsoleLog(prev => [...prev, `ðŸ“ˆ Overall Score: ${data.data.overallScore}/${data.data.maxScore}`]);
      setProgress(100);

      setResults(finalResults);
      setIsProcessing(false);
      setProcessingComplete(true);
      setIsSaved(false); // Mark as not saved yet

      toast.success('QEEG analysis completed successfully!');

    } catch (error) {
      console.error('Error processing QEEG files:', error);
      setConsoleLog(prev => [...prev, `âŒ Error: ${error.message}`]);
      setIsProcessing(false);
      toast.error(error.message || 'Failed to process QEEG files');
    }
  };

  const saveResultsToDatabase = async (resultData) => {
    if (!resultData) {
      toast.error('No results to save');
      return;
    }

    try {
      setIsSaving(true);
      toast.loading('Saving results to database...', { id: 'save-results' });

      console.log('ðŸ’¾ Saving results to database...');

      // PDF should already exist from auto-generation
      if (!pdfUrl) {
        console.warn('âš ï¸ No PDF URL available - PDF may not have been generated');
      }

      const algorithmResult = {
        id: `alg_${Date.now()}_${selectedPatient.id}`,
        patientId: selectedPatient.id,
        patientName: getPatientName(selectedPatient),
        clinicId: selectedPatient.clinicId,
        clinicName: selectedPatient.clinicName,
        pdfUrl: pdfUrl || null, // Use existing PDF URL from auto-generation
        results: resultData,
        eyesOpenFile: eyesOpenFile?.name,
        eyesClosedFile: eyesClosedFile?.name,
        processedAt: new Date().toISOString(),
        processedBy: 'super_admin' // Could be dynamic based on logged-in user
      };

      console.log('ðŸ’¾ Saving to database...');
      console.log('   Patient:', algorithmResult.patientName);
      console.log('   PDF URL:', pdfUrl || 'NOT SET');
      console.log('   PDF Type:', pdfUrl ? (pdfUrl.startsWith('http') ? 'Supabase' : 'Local') : 'None');

      await DatabaseService.add('algorithmResults', algorithmResult);

      setIsSaved(true);
      setIsSaving(false);

      toast.success('âœ… Results saved successfully!', { id: 'save-results' });

      // Reload history and patient list to update status
      loadProcessingHistory(selectedPatient.id);
      loadPatients();
    } catch (error) {
      console.error('âŒ Error saving results:', error);
      setIsSaving(false);
      toast.error('âŒ Failed to save results: ' + error.message, { id: 'save-results' });
    }
  };

  const handleSaveResults = async () => {
    if (results) {
      await saveResultsToDatabase(results);
      // After saving, automatically download PDF if available
      if (pdfUrl) {
        setTimeout(() => {
          handleDownloadPDF();
        }, 500); // Small delay to let save complete
      }
    }
  };

  const handleExecuteCalculation = () => {
    if (eyesOpenFile && eyesClosedFile) {
      processQEEGFiles();
    } else {
      toast.error('Please upload both QEEG data files');
    }
  };

  const handleExportResults = () => {
    if (results) {
      // Create CSV content
      const csvContent = [
        ['Parameter', 'Score (%)', 'Status'],
        ...results.map(r => [r.parameter, r.score, r.status])
      ].map(row => row.join(',')).join('\n');

      // Create and download file
      const blob = new Blob([csvContent], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'neurosense-algorithm-results.csv';
      a.click();
      window.URL.revokeObjectURL(url);
    }
  };

  const generatePDFReport = async (resultData) => {
    if (!resultData || !selectedPatient) {
      console.error('âŒ Cannot generate PDF: Missing resultData or selectedPatient');
      toast.error('Cannot generate PDF: Missing data');
      return null;
    }

    try {
      console.log('ðŸ”§ Preparing patient data...');

      // Prepare patient data
      const patientData = {
        name: getPatientName(selectedPatient),
        dateOfRecording: selectedPatient.dateOfBirth || new Date().toISOString().split('T')[0],
        age: selectedPatient.age || calculateAge(selectedPatient.dateOfBirth),
        gender: selectedPatient.gender || 'Not specified',
        handedness: selectedPatient.handedness || 'Not specified',
        patientId: selectedPatient.id,
        occupation: selectedPatient.occupation || 'Not specified',
        symptoms: selectedPatient.symptoms || []
      };

      console.log('ðŸ‘¤ Patient Data:', patientData);

      // Prepare algorithm results
      const algorithmResults = {
        parameters: resultData.map(result => ({
          name: result.parameter,
          score: parseInt(result.rawScore.split('/')[0]),
          maxScore: parseInt(result.rawScore.split('/')[1]),
          classification: result.status,
          metrics: result.metrics || []
        })),
        overallScore: resultData.reduce((sum, r) => sum + parseInt(r.rawScore.split('/')[0]), 0)
      };

      console.log('ðŸ“Š Algorithm Results:', algorithmResults);

      // Prepare QEEG data (use sample data if not available)
      const qeegData = {
        EC: {
          absolute: { Fz: { Delta: 5.2, Theta: 4.1, Alpha: 8.3, Beta: 6.2, HiBeta: 3.1 } },
          relative: { Pz: { Alpha: 45.2 } },
          special: { alphaPeak: 10.3, O1: 10.1 }
        },
        EO: {
          absolute: { Fz: { Theta: 3.8, Beta: 3.2, HiBeta: 2.5 } },
          relative: { Pz: { Alpha: 28.7 } }
        }
      };

      // Call backend API to generate PDF
      const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
      const pdfEndpoint = `${apiUrl}/qeeg/generate-pdf`;

      console.log('ðŸŒ Calling backend API:', pdfEndpoint);
      console.log('ðŸ“¡ Request payload size:', JSON.stringify({ patientData, algorithmResults, qeegData }).length, 'bytes');

      // Add timeout handling
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 30000); // 30 second timeout

      const response = await fetch(pdfEndpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          patientData,
          algorithmResults,
          qeegData
        }),
        signal: controller.signal
      }).catch(error => {
        clearTimeout(timeoutId);
        if (error.name === 'AbortError') {
          throw new Error('PDF generation timed out after 30 seconds. Server might be slow or not responding.');
        }
        throw error;
      });

      clearTimeout(timeoutId);

      console.log('ðŸ“¡ Backend response status:', response.status);
      console.log('ðŸ“¡ Backend response headers:', Object.fromEntries(response.headers.entries()));

      if (!response.ok) {
        let errorMessage = `Server returned ${response.status}`;
        try {
          const errorData = await response.json();
          errorMessage = errorData.message || errorMessage;
          console.error('âŒ Backend error data:', errorData);
        } catch (e) {
          const errorText = await response.text();
          errorMessage = errorText || errorMessage;
          console.error('âŒ Backend error text:', errorText);
        }
        throw new Error(`Failed to generate PDF: ${errorMessage}`);
      }

      const data = await response.json();
      console.log('ðŸ“¦ Backend response data:', data);

      if (!data || !data.success || !data.data) {
        console.error('âŒ Invalid response format:', data);
        throw new Error('Invalid response from server');
      }

      // Use the complete URL from backend instead of constructing it
      const pdfResult = {
        url: data.data.url, // Backend already provides complete URL
        path: data.data.path,
        filename: data.data.filename
      };

      console.log('âœ… PDF generated successfully!', pdfResult);
      console.log('âœ… PDF URL:', pdfResult.url);

      return pdfResult;

    } catch (error) {
      console.error('âŒ Error generating PDF:', error);
      console.error('âŒ Error name:', error.name);
      console.error('âŒ Error message:', error.message);

      if (error.message && error.stack) {
        console.error('âŒ Error stack:', error.stack);
      }

      // Provide specific error messages based on error type
      if (error.name === 'TypeError' && error.message.includes('Failed to fetch')) {
        console.error('âŒ NETWORK ERROR: Cannot connect to backend server');
        console.error('âŒ Please ensure backend server is running on http://localhost:3001');
        toast.error('Cannot connect to server. Please ensure backend is running on port 3001.');
      } else if (error.name === 'AbortError') {
        console.error('âŒ TIMEOUT ERROR: PDF generation took too long');
        toast.error('PDF generation timed out. Server might be overloaded.');
      } else {
        console.error('âŒ PDF GENERATION ERROR:', error.message);
        toast.error(`PDF generation failed: ${error.message}`);
      }

      return null;
    }
  };

  const handleDownloadPDF = async () => {
    if (pdfUrl) {
      try {
        toast.loading('Downloading PDF...', { id: 'download-pdf' });

        // Construct full backend URL
        const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
        const backendBaseUrl = apiUrl.replace('/api', ''); // Remove /api to get base URL
        const fullPdfUrl = pdfUrl.startsWith('http') ? pdfUrl : `${backendBaseUrl}${pdfUrl}`;

        console.log('ðŸ”½ Attempting to download from:', fullPdfUrl);

        // Fetch the PDF file
        const response = await fetch(fullPdfUrl);

        if (!response.ok) {
          console.error('âŒ Download failed with status:', response.status);
          throw new Error(`Server returned ${response.status}: ${response.statusText}`);
        }

        // Convert to blob
        const blob = await response.blob();

        // Verify blob is valid
        if (!blob || blob.size === 0) {
          throw new Error('Downloaded file is empty');
        }

        console.log('âœ… PDF fetched successfully, size:', blob.size, 'bytes');

        // Create download link
        const downloadUrl = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = downloadUrl;
        link.download = pdfUrl.split('/').pop() || 'neurosense-report.pdf';

        // Trigger download
        document.body.appendChild(link);
        link.click();

        // Cleanup
        document.body.removeChild(link);
        window.URL.revokeObjectURL(downloadUrl);

        toast.success('PDF downloaded successfully!', { id: 'download-pdf' });
      } catch (error) {
        console.error('âŒ Download error:', error);
        console.error('âŒ PDF URL:', pdfUrl);
        toast.error(`Failed to download PDF: ${error.message}`, { id: 'download-pdf' });
