                  const pdfCount = patientResults.filter(r => r.pdfUrl).length;
                  const totalCount = patientResults.length;

                  toast.success(`âœ… Synced! ${pdfCount}/${totalCount} PDFs available`, { id: 'sync-pdfs' });
