              title="Fetch PDFs from Supabase bucket and update database"
            >
              <Download className="h-3 w-3" />
              <span>Sync PDFs</span>
            </button>
          </div>
          <div className="space-y-3">
            {processingHistory.map((record, index) => (
              <div
                key={record.id}
                className="border border-gray-200 dark:border-gray-700 rounded-lg p-4 hover:bg-gray-50 dark:hover:bg-gray-700/50 transition-colors"
              >
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <Calendar className="h-4 w-4 text-primary" />
                      <span className="text-sm font-medium text-gray-900 dark:text-white">
                        {new Date(record.processedAt).toLocaleDateString('en-US', {
                          year: 'numeric',
                          month: 'short',
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </span>
                      {/* PDF Status Badge */}
                      {record.pdfUrl ? (
                        <span className="px-2 py-0.5 text-xs bg-green-100 text-green-700 dark:bg-green-900/30 dark:text-green-400 rounded-full flex items-center space-x-1">
                          <CheckCircle className="h-3 w-3" />
                          <span>PDF Available</span>
                        </span>
                      ) : (
                        <span className="px-2 py-0.5 text-xs bg-gray-100 text-gray-600 dark:bg-gray-700 dark:text-gray-400 rounded-full">
                          No PDF
                        </span>
                      )}
                    </div>
                    <div className="text-xs text-gray-600 dark:text-gray-400 space-y-1 mb-3">
                      <p>ðŸ“ Files: {record.eyesOpenFile}, {record.eyesClosedFile}</p>
                      <p>ðŸ‘¤ Processed by: {record.processedBy}</p>
                      {record.pdfUrl && (
                        <p className="text-green-600 dark:text-green-400">
                          ðŸ“„ PDF: {record.pdfUrl.startsWith('http') ? 'Supabase âœ“' : 'Local âœ“'}
                        </p>
                      )}
                      {!record.pdfUrl && (
                        <p className="text-red-600 dark:text-red-400">ðŸ“„ PDF: Not available</p>
                      )}
                    </div>

                    {/* Quick Summary of Results */}
                    {record.results && record.results.length > 0 && (
                      <div className="mt-3 grid grid-cols-2 gap-2">
                        {record.results.slice(0, 4).map((result, idx) => (
                          <div key={idx} className="text-xs bg-gray-50 dark:bg-gray-700 rounded p-2">
                            <span className="text-gray-600 dark:text-gray-400">{result.parameter}:</span>
                            <span className={`ml-2 font-semibold ${getStatusColor(result.rawScore)}`}>
                              {result.rawScore}
                            </span>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                  <div className="flex flex-col space-y-2">
                    {/* View Full Results Button */}
                    <button
                      onClick={() => {
                        setResults(record.results);
                        setProcessingComplete(true);
                        setIsSaved(true);
                        // Set PDF URL if available
                        if (record.pdfUrl) {
                          setPdfUrl(record.pdfUrl);
                        }
                        toast.success('Previous results loaded');
                      }}
                      className="px-3 py-2 text-sm bg-primary hover:bg-navy-700 text-white rounded-lg transition-colors flex items-center justify-center space-x-1"
                    >
                      <FileText className="h-4 w-4" />
                      <span>View Results</span>
                    </button>

                    {/* Download PDF Button - Always visible */}
                    <button
                      onClick={async () => {
                        if (!record.pdfUrl) {
                          toast.error('No PDF available for this record');
                          console.log('âŒ No PDF URL in record:', record);
                          return;
                        }

                        console.log('ðŸ“¥ Downloading PDF from record:', {
                          processedAt: record.processedAt,
                          pdfUrl: record.pdfUrl
                        });

                        try {
                          toast.loading('Downloading PDF...', { id: 'history-download' });

                          // Construct full URL (works with both local and Supabase URLs)
                          const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
                          const backendBaseUrl = apiUrl.replace('/api', '');
                          const fullPdfUrl = record.pdfUrl.startsWith('http') ? record.pdfUrl : `${backendBaseUrl}${record.pdfUrl}`;

                          console.log('ðŸ“¥ Downloading from:', fullPdfUrl);

                          // Fetch the PDF file
                          const response = await fetch(fullPdfUrl);
                          if (!response.ok) throw new Error('Failed to fetch PDF');

                          // Convert to blob
                          const blob = await response.blob();

                          // Create download link
                          const downloadUrl = window.URL.createObjectURL(blob);
                          const link = document.createElement('a');
                          link.href = downloadUrl;
                          link.download = record.pdfUrl.split('/').pop() || 'neurosense-report.pdf';

                          // Trigger download
                          document.body.appendChild(link);
                          link.click();

                          // Cleanup
                          document.body.removeChild(link);
                          window.URL.revokeObjectURL(downloadUrl);

                          toast.success('PDF downloaded successfully!', { id: 'history-download' });
                        } catch (error) {
                          console.error('Download error:', error);
                          toast.error('Failed to download PDF', { id: 'history-download' });
                        }
                      }}
                      disabled={!record.pdfUrl}
                      className={`px-3 py-2 text-sm rounded-lg transition-colors flex items-center justify-center space-x-1 ${
                        record.pdfUrl
                          ? 'bg-blue-600 hover:bg-blue-700 text-white cursor-pointer'
                          : 'bg-gray-300 dark:bg-gray-600 text-gray-500 dark:text-gray-400 cursor-not-allowed'
                      }`}
                      title={record.pdfUrl ? 'Download PDF Report' : 'No PDF available'}
                    >
                      <Download className="h-4 w-4" />
                      <span>Download PDF</span>
                    </button>
                  </div>
                </div>

                {/* Expandable Details */}
                {record.results && record.results.length > 4 && (
                  <div className="mt-3 pt-3 border-t border-gray-200 dark:border-gray-700 text-xs">
                    <div className="grid grid-cols-3 gap-2">
                      {record.results.slice(4).map((result, idx) => (
                        <div key={idx} className="flex justify-between">
                          <span className="text-gray-600 dark:text-gray-400">{result.parameter}:</span>
                          <span className="font-semibold">{result.rawScore}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
