                accept=".csv,.txt,.dat,.pdf"
                onChange={(e) => handleFileUpload('ec', e)}
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
              />
              <Upload className="h-12 w-12 text-gray-400 dark:text-gray-500 mx-auto mb-2" />
              <p className="text-sm text-gray-600 dark:text-gray-400">
                {eyesClosedFile ? eyesClosedFile.name : 'Drag & Drop or Click to Upload'}
              </p>
              {eyesClosedFile && (
                <CheckCircle className="h-6 w-6 text-green-500 mx-auto mt-2" />
              )}
            </div>
          </div>

          {/* Data Loaded Progress */}
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Data Loaded
            </label>
            <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2.5">
              <div
                className="bg-green-500 h-2.5 rounded-full transition-all duration-300"
                style={{ width: `${eyesOpenFile && eyesClosedFile ? 100 : eyesOpenFile || eyesClosedFile ? 50 : 0}%` }}
              ></div>
            </div>
          </div>
        </div>

        {/* Middle Panel - Algorithm Processing */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6">
          <h2 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
            Algorithm 1 Processing
          </h2>

          {/* Execute Button */}
          <button
            onClick={handleExecuteCalculation}
            disabled={!eyesOpenFile || !eyesClosedFile || isProcessing}
            className={`w-full mb-6 py-3 px-4 rounded-lg font-medium flex items-center justify-center space-x-2 transition-colors shadow-md ${
              eyesOpenFile && eyesClosedFile && !isProcessing
                ? 'bg-primary hover:bg-navy-700 text-white'
                : 'bg-gray-300 dark:bg-gray-600 text-gray-500 dark:text-gray-400 cursor-not-allowed'
            }`}
          >
            <Play className="h-5 w-5" />
            <span>Execute Calculation</span>
          </button>

          {/* Processing Animation */}
          {isProcessing && (
            <div className="bg-gradient-to-r from-primary to-navy-700 rounded-lg p-6 text-center mb-6 shadow-lg">
              <div className="relative inline-block">
                <Activity className="h-16 w-16 text-white animate-pulse" />
                <div className="absolute inset-0 flex items-center justify-center">
                  <div className="w-20 h-20 border-4 border-white/30 border-t-white rounded-full animate-spin"></div>
                </div>
              </div>
              <p className="text-white font-medium mt-4">Processing...</p>
              <p className="text-primary-light text-sm">{consoleLog.length} actions done</p>
            </div>
          )}

          {/* Processing Complete */}
          {processingComplete && (
            <div className="bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 rounded-lg p-4 text-center mb-6">
              <CheckCircle className="h-12 w-12 text-green-500 mx-auto mb-2" />
              <p className="text-green-800 dark:text-green-400 font-medium">Processing Complete!</p>
            </div>
          )}

          {/* Console */}
          <div className="bg-gray-900 dark:bg-black rounded-lg p-4 min-h-[200px] max-h-[300px] overflow-y-auto">
            <div className="flex items-center justify-between mb-2 pb-2 border-b border-gray-700">
              <h3 className="text-sm font-medium text-gray-300">Console</h3>
              <button
                onClick={() => setConsoleLog([])}
                className="text-xs text-gray-400 hover:text-gray-200"
              >
                Clear
              </button>
            </div>
            <div className="space-y-1 font-mono text-xs">
              {consoleLog.length > 0 ? (
                consoleLog.map((log, index) => (
                  <div key={index} className="text-green-400">
                    &gt; {log}
                  </div>
                ))
              ) : (
                <div className="text-gray-500 italic">Console output will appear here...</div>
              )}
            </div>
          </div>
        </div>

        {/* Right Panel - Final 7 Parameter Scores */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6">
          <h2 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
            Final 7 Parameter Scores
          </h2>

          {results ? (
            <>
              <div className="space-y-4 mb-6">
                {results.map((result, index) => (
                  <div key={index} className="border border-gray-200 dark:border-gray-700 rounded-lg p-3">
                    {/* Main Parameter */}
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-sm font-semibold text-gray-800 dark:text-gray-200">
                        {result.parameter}
                      </span>
                      <div className="flex items-center space-x-3">
                        <span className={`text-base font-bold ${getStatusColor(result.rawScore)}`}>
                          {result.rawScore}
                        </span>
                        <span className={`text-xs px-2.5 py-1 rounded-full font-medium ${getStatusBgColor(result.rawScore)}`}>
                          {result.status}
                        </span>
                      </div>
                    </div>

                    {/* Progress Bar */}
                    <div className="flex-1 bg-gray-200 dark:bg-gray-700 rounded-full h-2 mb-3">
                      <div
                        className={`h-2 rounded-full ${getProgressBarColor(result.rawScore)}`}
                        style={{ width: `${result.score}%` }}
                      ></div>
                    </div>

                    {/* Sub-Parameters */}
                    {result.metrics && result.metrics.length > 0 && (
                      <div className="mt-3 pt-3 border-t border-gray-200 dark:border-gray-700">
                        <div className="text-xs font-medium text-gray-600 dark:text-gray-400 mb-2">
                          Sub-Parameters:
                        </div>
                        <div className="space-y-2">
                          {result.metrics.map((metric, mIndex) => (
                            <div key={mIndex} className="flex items-center justify-between text-xs pl-2">
                              <span className="text-gray-600 dark:text-gray-400 flex-1">
                                {metric.name}
                              </span>
                              <div className="flex items-center space-x-2">
                                {metric.score === 1 ? (
                                  <CheckCircle className="h-3.5 w-3.5 text-green-500" />
                                ) : (
                                  <X className="h-3.5 w-3.5 text-red-500" />
                                )}
                                <span className={`font-medium ${metric.score === 1 ? 'text-green-600 dark:text-green-400' : 'text-red-600 dark:text-red-400'}`}>
                                  {metric.score}/1
                                </span>
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>

              {/* Action Buttons */}
              <div className="space-y-3">
                {/* Save and Download PDF Button */}
                <button
                  onClick={handleSaveResults}
                  disabled={isSaving || isSaved}
                  className={`w-full py-3 px-4 rounded-lg font-medium flex items-center justify-center space-x-2 transition-colors shadow-md ${
                    isSaved
                      ? 'bg-green-100 dark:bg-green-900/30 text-green-700 dark:text-green-400 cursor-not-allowed'
                      : isSaving
                      ? 'bg-blue-400 text-white cursor-wait'
                      : 'bg-primary hover:bg-navy-700 text-white'
                  }`}
                >
                  {isSaving ? (
                    <>
                      <Activity className="h-5 w-5 animate-spin" />
                      <span>Saving...</span>
                    </>
                  ) : isSaved ? (
                    <>
                      <CheckCircle className="h-5 w-5" />
                      <span>Saved âœ“</span>
                    </>
                  ) : (
                    <>
                      <Download className="h-5 w-5" />
                      <span>Save and Download PDF</span>
                    </>
                  )}
                </button>

                {/* Export CSV Button */}
                <button
                  onClick={handleExportResults}
                  className="w-full bg-green-600 hover:bg-green-700 text-white py-3 px-4 rounded-lg font-medium flex items-center justify-center space-x-2 transition-colors shadow-md"
                >
                  <Download className="h-5 w-5" />
                  <span>Export CSV</span>
                </button>

                {/* Single Download PDF Button */}
                {pdfUrl ? (
                  <button
                    onClick={handleDownloadPDF}
                    className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium flex items-center justify-center space-x-2 transition-colors shadow-md"
                  >
                    <Download className="h-5 w-5" />
                    <span>Download PDF Report</span>
                  </button>
                ) : (
                  <div className="w-full bg-gray-100 dark:bg-gray-700 border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg py-3 px-4 text-center">
                    <FileText className="h-5 w-5 text-gray-400 mx-auto mb-2" />
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      PDF will be generated automatically after processing
                    </p>
                  </div>
                )}
              </div>
            </>
          ) : (
            <div className="flex flex-col items-center justify-center py-12 text-center">
              <FileText className="h-16 w-16 text-gray-400 dark:text-gray-500 mb-4" />
              <p className="text-sm text-gray-600 dark:text-gray-400">
                Upload data files and execute calculation to see results
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Processing History */}
      {selectedPatient && processingHistory.length > 0 && (
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold text-gray-900 dark:text-white flex items-center">
              <History className="h-5 w-5 mr-2" />
              Processing History for {getPatientName(selectedPatient)}
            </h2>
            <button
              onClick={async () => {
                console.log('ðŸ”„ Manual sync triggered...');
                toast.loading('Syncing PDFs from Supabase...', { id: 'sync-pdfs' });

                try {
                  // Force reload from database and sync
                  await loadProcessingHistory(selectedPatient.id);

                  // Count how many PDFs were found
                  const results = await DatabaseService.get('algorithmResults');
                  const patientResults = results.filter(r => r.patientId === selectedPatient.id);
                  const pdfCount = patientResults.filter(r => r.pdfUrl).length;
                  const totalCount = patientResults.length;

                  toast.success(`âœ… Synced! ${pdfCount}/${totalCount} PDFs available`, { id: 'sync-pdfs' });
                  console.log(`âœ… Sync complete: ${pdfCount}/${totalCount} records have PDFs`);
                } catch (error) {
                  console.error('âŒ Sync failed:', error);
                  toast.error('Failed to sync PDFs', { id: 'sync-pdfs' });
                }
              }}
              className="px-3 py-1.5 text-xs bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors flex items-center space-x-1"
              title="Fetch PDFs from Supabase bucket and update database"
            >
              <Download className="h-3 w-3" />
              <span>Sync PDFs</span>
            </button>
          </div>
          <div className="space-y-3">
            {processingHistory.map((record, index) => (
              <div
                key={record.id}
                className="border border-gray-200 dark:border-gray-700 rounded-lg p-4 hover:bg-gray-50 dark:hover:bg-gray-700/50 transition-colors"
              >
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <Calendar className="h-4 w-4 text-primary" />
                      <span className="text-sm font-medium text-gray-900 dark:text-white">
                        {new Date(record.processedAt).toLocaleDateString('en-US', {
                          year: 'numeric',
                          month: 'short',
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </span>
                      {/* PDF Status Badge */}
                      {record.pdfUrl ? (
                        <span className="px-2 py-0.5 text-xs bg-green-100 text-green-700 dark:bg-green-900/30 dark:text-green-400 rounded-full flex items-center space-x-1">
                          <CheckCircle className="h-3 w-3" />
                          <span>PDF Available</span>
                        </span>
                      ) : (
                        <span className="px-2 py-0.5 text-xs bg-gray-100 text-gray-600 dark:bg-gray-700 dark:text-gray-400 rounded-full">
                          No PDF
                        </span>
                      )}
                    </div>
                    <div className="text-xs text-gray-600 dark:text-gray-400 space-y-1 mb-3">
                      <p>ðŸ“ Files: {record.eyesOpenFile}, {record.eyesClosedFile}</p>
                      <p>ðŸ‘¤ Processed by: {record.processedBy}</p>
                      {record.pdfUrl && (
                        <p className="text-green-600 dark:text-green-400">
                          ðŸ“„ PDF: {record.pdfUrl.startsWith('http') ? 'Supabase âœ“' : 'Local âœ“'}
                        </p>
                      )}
                      {!record.pdfUrl && (
                        <p className="text-red-600 dark:text-red-400">ðŸ“„ PDF: Not available</p>
                      )}
                    </div>

                    {/* Quick Summary of Results */}
                    {record.results && record.results.length > 0 && (
                      <div className="mt-3 grid grid-cols-2 gap-2">
                        {record.results.slice(0, 4).map((result, idx) => (
                          <div key={idx} className="text-xs bg-gray-50 dark:bg-gray-700 rounded p-2">
                            <span className="text-gray-600 dark:text-gray-400">{result.parameter}:</span>
                            <span className={`ml-2 font-semibold ${getStatusColor(result.rawScore)}`}>
                              {result.rawScore}
                            </span>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                  <div className="flex flex-col space-y-2">
                    {/* View Full Results Button */}
                    <button
                      onClick={() => {
                        setResults(record.results);
                        setProcessingComplete(true);
                        setIsSaved(true);
                        // Set PDF URL if available
                        if (record.pdfUrl) {
                          setPdfUrl(record.pdfUrl);
                        }
                        toast.success('Previous results loaded');
                      }}
                      className="px-3 py-2 text-sm bg-primary hover:bg-navy-700 text-white rounded-lg transition-colors flex items-center justify-center space-x-1"
                    >
                      <FileText className="h-4 w-4" />
                      <span>View Results</span>
                    </button>

                    {/* Download PDF Button - Always visible */}
                    <button
                      onClick={async () => {
                        if (!record.pdfUrl) {
                          toast.error('No PDF available for this record');
                          console.log('âŒ No PDF URL in record:', record);
                          return;
                        }

                        console.log('ðŸ“¥ Downloading PDF from record:', {
                          processedAt: record.processedAt,
                          pdfUrl: record.pdfUrl
                        });

                        try {
                          toast.loading('Downloading PDF...', { id: 'history-download' });

                          // Construct full URL (works with both local and Supabase URLs)
                          const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
                          const backendBaseUrl = apiUrl.replace('/api', '');
                          const fullPdfUrl = record.pdfUrl.startsWith('http') ? record.pdfUrl : `${backendBaseUrl}${record.pdfUrl}`;

                          console.log('ðŸ“¥ Downloading from:', fullPdfUrl);

                          // Fetch the PDF file
                          const response = await fetch(fullPdfUrl);
                          if (!response.ok) throw new Error('Failed to fetch PDF');

                          // Convert to blob
                          const blob = await response.blob();

                          // Create download link
                          const downloadUrl = window.URL.createObjectURL(blob);
                          const link = document.createElement('a');
                          link.href = downloadUrl;
                          link.download = record.pdfUrl.split('/').pop() || 'neurosense-report.pdf';

                          // Trigger download
                          document.body.appendChild(link);
                          link.click();

                          // Cleanup
                          document.body.removeChild(link);
                          window.URL.revokeObjectURL(downloadUrl);

                          toast.success('PDF downloaded successfully!', { id: 'history-download' });
                        } catch (error) {
                          console.error('Download error:', error);
                          toast.error('Failed to download PDF', { id: 'history-download' });
                        }
                      }}
                      disabled={!record.pdfUrl}
                      className={`px-3 py-2 text-sm rounded-lg transition-colors flex items-center justify-center space-x-1 ${
                        record.pdfUrl
                          ? 'bg-blue-600 hover:bg-blue-700 text-white cursor-pointer'
                          : 'bg-gray-300 dark:bg-gray-600 text-gray-500 dark:text-gray-400 cursor-not-allowed'
                      }`}
                      title={record.pdfUrl ? 'Download PDF Report' : 'No PDF available'}
                    >
                      <Download className="h-4 w-4" />
                      <span>Download PDF</span>
                    </button>
                  </div>
                </div>

                {/* Expandable Details */}
                {record.results && record.results.length > 4 && (
                  <div className="mt-3 pt-3 border-t border-gray-200 dark:border-gray-700 text-xs">
                    <div className="grid grid-cols-3 gap-2">
                      {record.results.slice(4).map((result, idx) => (
                        <div key={idx} className="flex justify-between">
                          <span className="text-gray-600 dark:text-gray-400">{result.parameter}:</span>
                          <span className="font-semibold">{result.rawScore}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Info Section */}
      <div className="bg-accent-light dark:bg-primary/20 border border-primary-light dark:border-primary rounded-lg p-4 shadow-sm">
        <h3 className="text-sm font-medium text-primary dark:text-primary-light mb-2">
          About Algorithm 1 Processing
        </h3>
        <p className="text-sm text-navy-800 dark:text-gray-300">
          This processor analyzes QEEG data files (Eyes Open and Eyes Closed) and generates 7 key parameters using proprietary
          binary scoring logic. The algorithm evaluates brain activity patterns to assess cognitive and emotional health markers.
        </p>
      </div>
    </div>
  );
};

export default AlgorithmDataProcessor;

