/**
 * Enhanced AI-Powered PDF Report Generator
 * Uses OpenAI to generate content + PDFKit to create beautiful PDFs
 *
 * Features:
 * - Page 1: Blue gradient background with NeuroSense branding
 * - Page 2: Parameter scores with visual cards
 * - AI-generated content for insights and recommendations
 */

const PDFDocument = require('pdfkit');
const OpenAI = require('openai');
const fs = require('fs');
const path = require('path');

// Initialize OpenAI client
const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY
});

// Color scheme
const COLORS = {
  primary: '#4A90E2',        // Primary Blue
  primaryLight: '#67A3E9',   // Lighter Blue
  primaryDark: '#2E5C8A',    // Darker Blue
  teal: '#7DD3C0',           // Teal/Turquoise
  white: '#FFFFFF',
  black: '#000000',
  darkGray: '#4A4A4A',
  lightGray: '#F5F5F5',
  green: '#2ECC71',          // High score
  orange: '#E67E22',         // Low score
  blue: '#3498DB'            // Medium score
};

// Fonts
const FONTS = {
  regular: 'Helvetica',
  bold: 'Helvetica-Bold',
  italic: 'Helvetica-Oblique',
  boldItalic: 'Helvetica-BoldOblique'
};

class EnhancedAIPdfGenerator {
  constructor(patientData, algorithmResults, qeegData) {
    this.patientData = patientData;
    this.algorithmResults = algorithmResults;
    this.qeegData = qeegData;
    this.doc = null;
  }

  /**
   * Generate complete PDF report with AI insights
   */
  async generateReport(outputPath) {
    return new Promise(async (resolve, reject) => {
      try {
        console.log('\nðŸ¤– === Enhanced AI PDF Generation Started ===\n');
        console.log('ðŸ‘¤ Patient:', this.patientData.name);
        console.log('ðŸ“Š Parameters:', this.algorithmResults.parameters.length);
        console.log('ðŸŽ¯ Overall Score:', this.algorithmResults.overallScore);

        // Step 1: Create PDF document
        this.doc = new PDFDocument({
          size: 'A4',
          margins: {
            top: 0,
            bottom: 0,
            left: 0,
            right: 0
          },
          bufferPages: true,
          autoFirstPage: false
        });

        // Create write stream
        const stream = fs.createWriteStream(outputPath);
        this.doc.pipe(stream);

        // Step 2: Generate AI insights (parallel to PDF creation)
        console.log('ðŸ§  Generating AI insights...');
        const aiInsights = await this.generateAIInsights();
        console.log('âœ… AI insights generated');

        // Step 3: Generate PDF pages
        console.log('ðŸ“„ Creating Page 1: Cover with branding...');
        this.generateCoverPage();

        console.log('ðŸ“„ Creating Page 2: Parameter Scores...');
        this.generateParametersPage();

        console.log('ðŸ“„ Creating Page 3: AI Insights & Recommendations...');
        this.generateInsightsPage(aiInsights);

        // Finalize PDF
        this.doc.end();

        // Handle completion
        stream.on('finish', () => {
          console.log('âœ… Enhanced AI PDF generated successfully:', outputPath);
          const stats = fs.statSync(outputPath);
          console.log('ðŸ“Š File size:', (stats.size / 1024).toFixed(2), 'KB');
          resolve(outputPath);
        });

        stream.on('error', (error) => {
          console.error('âŒ Error writing PDF:', error);
          reject(error);
        });

      } catch (error) {
        console.error('âŒ Error generating AI PDF:', error);
        reject(error);
      }
    });
  }

  /**
   * PAGE 1: Cover Page with Blue Gradient Background
   */
  generateCoverPage() {
    this.doc.addPage();

    const pageWidth = 595.28;   // A4 width
    const pageHeight = 841.89;  // A4 height
    const centerX = pageWidth / 2;

    // === BLUE GRADIENT BACKGROUND (Top 70%) ===
    const blueHeight = pageHeight * 0.70;

    // Draw gradient rectangles from top to middle
    const gradientSteps = 100;
    for (let i = 0; i < gradientSteps; i++) {
