    const percentage = Math.round((overallScore / maxScore) * 100);

    this.doc.fontSize(20)
      .fillColor(COLORS.white)
      .font(FONTS.bold)
      .text(
        `Overall Brain Health Score: ${overallScore}/${maxScore} (${percentage}%)`,
        margin + 20,
        yPos + 20,
        { width: contentWidth - 40 }
      );

    yPos += scoreBoxHeight + 30;

    // === INTRO TEXT ===
    this.doc.fontSize(11)
      .fillColor(COLORS.darkGray)
      .font(FONTS.regular)
      .text(
        'Below are your 7 brain health parameters, each scored from 0-3 where higher scores indicate better performance.',
