        { width: contentWidth, align: 'justify' }
      );

    yPos += 40;

    // === PARAMETER CARDS ===
    const parameters = this.algorithmResults.parameters || [];
    const cardWidth = contentWidth;
    const cardHeight = 70;
    const cardSpacing = 10;

    parameters.forEach((param, index) => {
      // Check if we need a new page
      if (yPos + cardHeight > 750) {
        this.doc.addPage();
        yPos = margin;
      }

      // Card background
      const bgColor = index % 2 === 0 ? COLORS.lightGray : COLORS.white;
      this.doc.roundedRect(margin, yPos, cardWidth, cardHeight, 8)
        .fillColor(bgColor)
        .fill();

      // Border for emphasis
      this.doc.roundedRect(margin, yPos, cardWidth, cardHeight, 8)
        .strokeColor(COLORS.primary, 0.3)
        .lineWidth(1)
        .stroke();

