      this.doc.addPage();
      yPos = margin;
    }

    const brainTypePattern = parameters.map(p => `${p.name} ${p.classification.charAt(0)}`).join(' Â· ');

    this.doc.fontSize(12)
      .fillColor(COLORS.primary)
      .font(FONTS.bold)
      .text('Brain-Type Pattern:', margin, yPos);

    yPos += 20;

    this.doc.fontSize(10)
