  this.doc.circle(x + 20, yPos + 10, 12)
    .fillColor(COLORS.primary)
    .fill();

  this.doc.fontSize(12)
    .fillColor(COLORS.white)
    .font(FONTS.bold)
    .text(`${number}`, x + 15, yPos + 5, { width: 10, align: 'center' });

  // Parameter name
  this.doc.fontSize(15)
