  this.doc.fontSize(11)
    .fillColor(COLORS.darkGray)
    .font(FONTS.regular)
    .text('[Score: ', scoreX, yPos + 5, { continued: true });

  this.doc.fontSize(13)
    .fillColor(COLORS.primary)
    .font(FONTS.bold)
    .text(scoreText, { continued: true });

  this.doc.fontSize(11)
    .fillColor(COLORS.darkGray)
    .font(FONTS.regular)
