    75          console.log('INFO: All patients data:', allPatients);
    76          console.log('INFO: All patient emails:', allPatients.map(p => p.email));
    77
    78          // More robust email matching with trim
    79          const userEmailLower = user.email.trim().toLowerCase();
    80          patientRecord = allPatients.find(p => {
    81            if (!p.email) return false;
    82            const patientEmailLower = p.email.trim().toLowerCase();
    83            console.log(`DEBUG: Comparing: "${patientEmailLower}" === "${userEmailLower}"`, patientEmailLower === userEmailLower);
    84            return patientEmailLower === userEmailLower;
    85          });
    86
    87          console.log('INFO: Patient record from DB (by email):', patientRecord);
    88        }
    89
    90        if (patientRecord) {
    91          console.log('SUCCESS: Patient record found!');
    92          console.log('INFO: Patient record fields:', Object.keys(patientRecord));
    93          console.log('INFO: Full patient record:', patientRecord);
    94
    95          // Fetch clinic data if patient has clinic_id or org_id
    96          let clinicData = null;
    97          const clinicId = patientRecord.clinicId || patientRecord.clinic_id || patientRecord.orgId || patientRecord.org_id || patientRecord.ownerId || patientRecord.owner_id;
    98
    99          console.log('DEBUG: Looking for clinic with ID:', clinicId);
   100          console.log('DEBUG: Patient record keys:', Object.keys(patientRecord));
   101
   102          if (clinicId) {
   103            try {
   104              clinicData = await DatabaseService.findById('clinics', clinicId);
   105              console.log('CLINIC: Clinic data from DB:', clinicData);
   106            } catch (clinicError) {
   107              console.warn('WARNING: Failed to fetch clinic data:', clinicError);
   108            }
   109          } else {
   110            console.warn('WARNING: No clinic ID found in patient record');
   111          }
   112
   113          // Update patient data state with real data from database
   114          const updatedData = {
