   117              email: patientRecord.email || user.email || 'N/A',
   118              phone: patientRecord.phone || 'Not provided',
   119              dateOfBirth: patientRecord.dateOfBirth || patientRecord.date_of_birth || 'Not provided',
   120              address: patientRecord.address || 'Not provided',
   121              emergencyContact: patientRecord.emergencyContact || patientRecord.emergency_contact || 'Not provided'
   122            },
   123            clinic: clinicData ? {
   124              name: clinicData.name || 'N/A',
   125              address: clinicData.address || 'N/A',
   126              phone: clinicData.phone || 'N/A',
   127              email: clinicData.email || 'N/A',
   128              doctorName: clinicData.primaryDoctor || clinicData.primary_doctor || 'Not assigned'
