   130              name: 'No clinic assigned',
   131              address: 'N/A',
   132              phone: 'N/A',
   133              email: 'N/A',
   134              doctorName: 'N/A'
   135            }
   136            // Note: Keep existing reports, carePlans, resources from initial state
   137          };
