   171                    emergencyContact: patientByEmail.emergencyContact || patientByEmail.emergency_contact || 'Not provided'
   172                  },
   173                  clinic: clinicData ? {
   174                    name: clinicData.name || 'N/A',
   175                    address: clinicData.address || 'N/A',
   176                    phone: clinicData.phone || 'N/A',
   177                    email: clinicData.email || 'N/A',
   178                    doctorName: clinicData.primaryDoctor || clinicData.primary_doctor || 'Not assigned'
   179                  } : prevData.clinic
   180                }));
   181              } else {
   182                console.warn('WARNING: Patient not found by email either, using user data as fallback');
   183                // Use user data as fallback
   184                setPatientData(prevData => ({
   185                  ...prevData,
   186                  profile: {
   187                    name: user.name || user.full_name || 'Patient',
   188                    email: user.email || 'Not provided',
   189                    phone: user.phone || 'Not provided',
   190                    dateOfBirth: user.dateOfBirth || user.date_of_birth || 'Not provided',
   191                    address: user.address || 'Not provided',
   192                    emergencyContact: user.emergencyContact || user.emergency_contact || 'Not provided'
   193                  },
   194                  clinic: {
   195                    name: 'No clinic assigned',
   196                    address: 'N/A',
   197                    phone: 'N/A',
   198                    email: 'N/A',
   199                    doctorName: 'Not assigned'
   200                  }
   201                }));
   202              }
   203            } catch (emailError) {
   204              console.error('ERROR: Failed to find patient by email:', emailError);
   205              // Final fallback - use user object directly
   206              setPatientData(prevData => ({
   207                ...prevData,
   208                profile: {
   209                  name: user.name || user.full_name || 'Patient',
   210                  email: user.email || 'Not provided',
   211                  phone: user.phone || 'Not provided',
   212                  dateOfBirth: user.dateOfBirth || user.date_of_birth || 'Not provided',
   213                  address: user.address || 'Not provided',
   214                  emergencyContact: user.emergencyContact || user.emergency_contact || 'Not provided'
   215                },
   216                clinic: {
   217                  name: 'No clinic assigned',
   218                  address: 'N/A',
   219                  phone: 'N/A',
   220                  email: 'N/A',
   221                  doctorName: 'Not assigned'
   222                }
   223              }));
   224            }
   225          } else {
   226            // No email to search with - use user data
   227            console.warn('WARNING: No email available for search, using user data');
   228            setPatientData(prevData => ({
   229              ...prevData,
   230              profile: {
   231                name: user.name || user.full_name || 'Patient',
   232                email: user.email || 'Not provided',
   233                phone: user.phone || 'Not provided',
   234                dateOfBirth: user.dateOfBirth || user.date_of_birth || 'Not provided',
   235                address: user.address || 'Not provided',
   236                emergencyContact: user.emergencyContact || user.emergency_contact || 'Not provided'
   237              },
   238              clinic: {
   239                name: 'No clinic assigned',
   240                address: 'N/A',
   241                phone: 'N/A',
   242                email: 'N/A',
   243                doctorName: 'Not assigned'
   244              }
   245            }));
   246          }
   247        }
   248
   249        // Disable appointments and progress tracking for now (tables not available)
   250        // These features can be enabled when the corresponding tables are created
   251
   252      } catch (error) {
   253        console.error('ERROR: Failed to load patient data:', error);
   254        // Fallback on error - use user data
   255        setPatientData(prevData => ({
