   281  // Handle appointment booking
   282  const handleBookAppointment = async () => {
   283    try {
   284      // Simple booking - book next available follow-up slot
   285      const tomorrow = new Date();
   286      tomorrow.setDate(tomorrow.getDate() + 1);
   287      const dateStr = tomorrow.toISOString().split('T')[0];
   288
   289      // Get available slots
   290      const availableSlots = await bookingService.getAvailableSlots(
   291        user?.clinic_id || 'default-clinic',
   292        dateStr,
   293        'follow-up'
   294      );
   295
   296      if (availableSlots.length === 0) {
   297        alert('No available slots for tomorrow. Please try a different date.');
   298        return;
   299      }
   300
   301      // Book the first available slot
   302      const firstSlot = availableSlots.find(slot => slot.available);
   303      if (!firstSlot) {
   304        alert('No available slots found. Please contact the clinic directly.');
   305        return;
   306      }
   307
   308      const appointmentData = {
   309        patientId: user.id,
   310        clinicId: user?.clinic_id || 'default-clinic',
   311        appointmentType: 'follow-up',
   312        date: dateStr,
   313        time: firstSlot.time,
   314        requestedBy: user.id,
   315        notes: 'Requested via patient portal'
   316      };
   317
   318      const newAppointment = await bookingService.bookAppointment(appointmentData);
   319
   320      // Refresh appointments list
   321      const updatedAppointments = await bookingService.getPatientAppointments(user.id);
   322      setAppointments(updatedAppointments);
   323
   324      alert(`Appointment booked successfully for ${dateStr} at ${firstSlot.display}!`);
   325      console.log('SUCCESS: Appointment booked:', newAppointment);
   326
   327    } catch (error) {
   328      console.error('ERROR: Failed to book appointment:', error);
   329      alert(`Failed to book appointment: ${error.message}`);
