    97→
    98→      console.log(`📋 Loaded ${patientResults.length} records for patient ${patientId}`);
    99→
   100→      // Log all records for debugging
   101→      console.log('📊 All records for this patient:');
   102→      patientResults.forEach((rec, idx) => {
   103→        console.log(`   ${idx + 1}. ${new Date(rec.processedAt).toLocaleString()} - PDF: ${rec.pdfUrl ? '✓' : '✗'}`);
   104→      });
