   128→
   129→              console.log(`\n   Record: ${record.patientName} @ ${new Date(record.processedAt).toLocaleString()}`);
   130→              console.log(`   Searching for PDFs matching: neurosense-report-${sanitizedName}-*.pdf`);
   131→
   132→              // Find PDFs matching patient name
