   138→
   139→              console.log(`   Found ${matchingPdfs.length} PDFs for this patient`);
   140→
   141→              if (matchingPdfs.length > 0) {
   142→                // Find PDF with closest timestamp (within 60 seconds)
   143→                let bestMatch = null;
   144→                let smallestDiff = Infinity;
   145→
   146→                for (const pdf of matchingPdfs) {
   147→                  // Extract timestamp from filename: neurosense-report-name-TIMESTAMP.pdf
   148→                  const match = pdf.name.match(/-(\d+)\.pdf$/);
   149→                  if (match) {
   150→                    const pdfTimestamp = parseInt(match[1]);
   151→                    const diff = Math.abs(pdfTimestamp - recordTimestamp);
   152→                    console.log(`      ${pdf.name}: timestamp diff = ${diff}ms (${(diff/1000).toFixed(1)}s)`);
   153→
   154→                    // Accept if within 60 seconds (60000ms)
   155→                    if (diff < 60000 && diff < smallestDiff) {
   156→                      smallestDiff = diff;
   157→                      bestMatch = pdf;
   158→                    }
   159→                  }
   160→                }
   161→
   162→                if (bestMatch) {
   163→                  console.log(`   ✅ MATCH FOUND! ${bestMatch.name} (${(smallestDiff/1000).toFixed(1)}s difference)`);
   164→                  console.log(`   URL: ${bestMatch.url}`);
   165→                  record.pdfUrl = bestMatch.url;
   166→                  await DatabaseService.update('algorithmResults', record.id, record);
   167→                  updatedCount++;
   168→                } else {
   169→                  console.log(`   ⚠️ PDFs found but timestamp difference too large (>60s)`);
   170→                }
   171→              } else {
   172→                console.log(`   ❌ No PDFs found for patient: ${sanitizedName}`);
   173→              }
   174→            }
   175→          }
   176→        } catch (supabaseErr) {
