   186→            const sanitizedName = record.patientName.replace(/[^a-z0-9]/gi, '_').toLowerCase();
   187→            const possibleFilename = `neurosense-report-${sanitizedName}-${timestamp}.pdf`;
   188→            const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';
