   820→  const calculateAge = (dateOfBirth) => {
   821→    if (!dateOfBirth) return 'N/A';
   822→    const today = new Date();
   823→    const birthDate = new Date(dateOfBirth);
   824→    let age = today.getFullYear() - birthDate.getFullYear();
   825→    const monthDiff = today.getMonth() - birthDate.getMonth();
   826→    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
   827→      age--;
   828→    }
   829→    return age;
   830→  };
