        results: results.parameters,
        overallScore: results.overallScore,
        maxScore: 21,
        pdfUrl: pdfUrl,
        pdfFilename: pdfFilename
      }
    });

  } catch (error) {
    console.error('\n❌ === QEEG Processing Failed ===');
