/**
 * GET /api/qeeg/supabase-pdfs
 * List all PDFs from Supabase bucket
 */
router.get('/supabase-pdfs', async (req, res) => {
  try {
    console.log('📋 Fetching PDFs from Supabase bucket...');

    const files = await SupabaseStorage.listFiles('neurosense_bucketreports', 'reports');

    // Get public URLs for all files
    const pdfs = files.map(file => {
      const { createClient } = require('@supabase/supabase-js');
      const supabase = createClient(process.env.SUPABASE_URL, process.env.SUPABASE_SERVICE_ROLE_KEY);
      const { data: urlData } = supabase.storage
        .from('neurosense_bucketreports')
        .getPublicUrl(`reports/${file.name}`);

      return {
        name: file.name,
        url: urlData.publicUrl,
        created_at: file.created_at,
        size: file.metadata?.size
      };
    });

    console.log(`✅ Found ${pdfs.length} PDFs in Supabase bucket`);

    res.json({
      success: true,
      count: pdfs.length,
      pdfs: pdfs
    });

  } catch (error) {
    console.error('❌ Error listing Supabase PDFs:', error);
    res.status(500).json({
      success: false,
      error: error.message
    });
  }
});

