  clearTimeout(timeoutId);
  if (fetchError.name === 'AbortError') {
    throw new Error('Upload timed out. The file may be too large or the server is busy. Please try again.');
  }
  // Check if it's a network error (backend not running)
  if (fetchError.message.includes('Failed to fetch') || fetchError.message.includes('fetch')) {
    throw new Error('Cannot connect to backend server. Please ensure the backend is running on port 3001. Run "npm run dev:backend" to start the server.');
  }
  throw fetchError;
}

