{clinic && (
  <div>
    <label className="block text-sm font-medium text-gray-700 mb-1">
      Current Password
    </label>
    <div className="flex items-center space-x-2">
      <input
        type={showPassword ? "text" : "password"}
        value={clinic.password || 'Not Set'}
        readOnly
        className="flex-1 px-3 py-2 border border-gray-300 rounded-md bg-gray-50 font-mono text-sm"
      />
      <button
        type="button"
        onClick={() => setShowPassword(!showPassword)}
        className="p-2 text-gray-500 hover:text-gray-700 border border-gray-300 rounded-md hover:bg-gray-50"
        title={showPassword ? "Hide password" : "Show password"}
      >
        {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
      </button>
      <button
        type="button"
        onClick={async () => {
          try {
            const password = clinic.password || '';
            if (navigator.clipboard && window.isSecureContext) {
              await navigator.clipboard.writeText(password);
              toast.success('Password copied to clipboard!');
            } else {
              // Fallback for non-secure context
              const textArea = document.createElement('textarea');
              textArea.value = password;
              textArea.style.position = 'fixed';
              textArea.style.left = '-999999px';
              textArea.style.top = '-999999px';
              document.body.appendChild(textArea);
              textArea.focus();
              textArea.select();
              document.execCommand('copy');
              document.body.removeChild(textArea);
              toast.success('Password copied to clipboard!');
            }
          } catch (error) {
            console.warn('Could not copy to clipboard:', error);
            toast.error('Could not copy password');
          }
        }}
        className="p-2 text-gray-500 hover:text-gray-700 border border-gray-300 rounded-md hover:bg-gray-50"
        title="Copy password to clipboard"
      >
        <Copy className="h-4 w-4" />
      </button>
    </div>
    <p className="text-xs text-gray-500 mt-1">
      Current login password for this clinic. Use "Reset Password" button to change.
    </p>
  </div>
)}

