                        <button
                          onClick={() => openPatientViewModal(patient)}
                          className="text-green-600 dark:text-green-400 hover:text-green-900 dark:hover:text-green-300 transition-colors"
                          title="View Clinical Report"
                        >
                          <Eye className="h-4 w-4" />
                        </button>
                        <button
                          onClick={() => openModal(patient)}
                          className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200"
                          title="Edit Patient"
                        >
                          <Edit className="h-4 w-4" />
                        </button>
                        <button
                          onClick={() => handleDeletePatient(patient.id)}
                          className="text-red-600 dark:text-red-400 hover:text-red-900 dark:hover:text-red-300"
                          title="Delete Patient"
                        >
                          <Trash2 className="h-4 w-4" />
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>

          {filteredPatients.length === 0 && (
            <div className="text-center py-12">
              <Users className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-600 mb-4">
                {searchTerm || genderFilter 
                  ? 'No patients match your filters' 
                  : 'No patients added yet'
                }
              </p>
              <button
                onClick={() => openModal()}
                className="bg-[#323956] hover:bg-[#232D3C] text-white px-4 py-2 rounded-lg font-medium shadow-md"
              >
                Add First Patient
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <PatientModal
          patient={selectedPatient}
          onSubmit={selectedPatient ? handleEditPatient : handleCreatePatient}
          onClose={closeModal}
          register={register}
          handleSubmit={handleSubmit}
          errors={errors}
        />
      )}

      {/* Upload Report Modal */}
      {showUploadModal && (
        <UploadReportModal
          clinicId={clinicId}
          patient={patientForUpload}
          onUpload={() => {
            loadPatients();
            closeUploadModal();
            onUpdate?.();
          }}
          onClose={closeUploadModal}
        />
      )}

      {/* Patient List Modal */}
      {showPatientListModal && (
        <PatientListModal
          onAddPatients={handleBulkAddPatients}
          onClose={closePatientListModal}
        />
      )}

      {/* Patient Information Modal - Clinical Report Form */}
      {showPatientInfoModal && patientForInfo && (
        <ClinicalReportForm
          patient={patientForInfo}
          onClose={closePatientInfoModal}
          onSave={async (formData, files) => {
            // Save clinical report data
            console.log('Saving clinical report:', formData, files);
            await loadPatients();
            onUpdate?.();
          }}
        />
      )}

      {/* Patient Clinical Report View Modal */}
      {showPatientViewModal && patientForView && (
        <ClinicalReportView
          patient={patientForView}
          onClose={closePatientViewModal}
        />
      )}
    </div>
  );
};

